/* groupware.vacation.selectVacationDayList */ SELECT UR_Code, YEAR, DeptName, JobPositionName, DisplayName, EnterDate, RetireDate, CompanyCode ,ExpDateStart ,ExpDateEnd, Reason, UseStartDate, UseEndDate, RegisterCode ,CAST(VacDay AS FLOAT) AS VacDay ,CAST(UseVacDay AS FLOAT) AS UseVacDay ,CAST(VacDay - UseVacDay AS FLOAT) AS RemainVacDay ,CAST(LastVacDay as FLOAT) as LastVacDay ,CAST(LongVacDay as FLOAT) as LongVacDay FROM ( SELECT A.UR_Code , A.YEAR , cast(cast(FORMAT(A.VacDay,3) as DECIMAL(9,3)) as float) AS VacDay , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , Fn_BaseGetDictionary_S(#{lang}, u.MultiDisplayName) AS DisplayName , u.EnterDate , u.RetireDate , B.CompanyCode , DATE_FORMAT(STR_TO_DATE(A.UseStartDate, '%Y%m%d'), '%y.%m.%d') AS ExpDateStart , DATE_FORMAT(STR_TO_DATE(A.UseEndDate, '%Y%m%d'), '%y.%m.%d') AS ExpDateEnd , A.UseStartDate , A.UseEndDate , A.RegisterCode , (SELECT IFNULL(SUM(u.VacDay), 0) FROM vm_vacationinfo_day u LEFT JOIN SYS_BASE_CODE k ON u.VacFlag = k.CODE AND k.CodeGroup = 'VACATION_TYPE' AND k.IsUse ='Y' AND k.DomainID = #{domainID} AND k.Reserved1 ='+' LEFT JOIN SYS_BASE_CODE t ON k.ReservedInt = t.CodeID WHERE A.ur_code = u.UR_Code AND u.VacDate BETWEEN DATE_FORMAT(a.UseStartDate, '%Y-%m-%d') AND DATE_FORMAT(a.UseEndDate , '%Y-%m-%d') AND A.VacKind = t.Code) AS UseVacDay , A.VmComment AS Reason , A.LastVacDay , A.LongVacDay FROM vm_vacationplan A INNER JOIN sys_object_user u on A.UR_Code = u.UserCode INNER JOIN sys_object_user_basegroup B ON u.UserCode = B.UserCode AND JobType = 'Origin' WHERE A.year = #{year}
AND B.CompanyCode = #{domainCode}
and A.VacKind='PUBLIC'
AND B.DeptName LIKE CONCAT('%', #{schTxt}, '%')
AND DisplayName LIKE CONCAT('%', #{schTxt}, '%')
AND U.RetireDate = '' AND B.DeptCode NOT LIKE '%RetireDept'
AND U.RetireDate != ''
AND A.UR_CODE = #{urCode}
B.JobTitleSortKey ASC
B.JobLevelSortKey ASC
B.JobPositionSortKey ASC
DisplayName ASC
, B.SortKey ASC
DeptName DisplayName EnterDate JobPositionName ExpDateStart VacDay B.SortKey
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}
) TBL

