SELECT UR_Code, YEAR, VacDay, DeptName, JobPositionName, DisplayName, EnterDate, RetireDate , CompanyCode, ExpDateStart, ExpDateEnd, UseStartDate, UseEndDate, RegisterCode , cast(cast(UseVacDay as DECIMAL(9,3)) as float) AS UseVacDay , (VacDay - UseVacDay) AS RemainVacDay, Reason FROM( SELECT A.UR_Code , A.YEAR , cast(cast(FORMAT(A.VacDay,3) as DECIMAL(9,3)) as float) AS VacDay , B.DeptName , B.DeptCode , B.JobPositionName , B.JobPositionSortKey , B.SortKey , B.DisplayName , B.EnterDate , B.RetireDate , B.CompanyCode , DATE_FORMAT(STR_TO_DATE(A.UseStartDate,'%Y%m%d'),'%y.%m.%d') AS ExpDateStart , DATE_FORMAT(STR_TO_DATE(A.UseEndDate,'%Y%m%d'),'%y.%m.%d') AS ExpDateEnd , A.UseStartDate , A.UseEndDate , A.RegisterCode , (SELECT IFNULL(SUM(VacDay), 0) FROM covi_smart4j.VM_VACATIONINFO WHERE STR_TO_DATE(Sdate,'%Y-%m-%d') between STR_TO_DATE(A.UseStartDate,'%Y%m%d') and STR_TO_DATE(A.UseEndDate,'%Y%m%d') AND STR_TO_DATE(Edate,'%Y-%m-%d') between STR_TO_DATE(A.UseStartDate,'%Y%m%d') and STR_TO_DATE(A.UseEndDate,'%Y%m%d') AND UR_Code = A.UR_Code AND GUBUN in ('VACATION_APPLY','VACATION_PUBLIC','VACATION_CANCEL','VACATION_PUBLIC_CANCEL') AND VacFlag IN (SELECT A.Code FROM ( SELECT * FROM covi_smart4j.SYS_BASE_CODE WHERE CodeGroup = 'VACATION_TYPE' AND IsUse = 'Y' AND Reserved1 = '+' AND DomainID = #{domainID}
) A, ( SELECT * FROM covi_smart4j.SYS_BASE_CODE WHERE CodeGroup = 'VACATION_KIND' AND IsUse = 'Y' AND DomainID = 0 AND Code = 'PUBLIC' ) B WHERE A.ReservedInt = B.CodeID ) ) AS UseVacDay , A.VmComment AS Reason FROM covi_smart4j.vm_vacationplan A INNER JOIN ( SELECT A.UserCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , A.EnterDate , A.RetireDate , A.SortKey , B.CompanyCode , B.DeptCode , Fn_BaseGetDictionary_S(#{lang},IFNULL(B.DeptName, B.MultiDeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , B.JobPositionSortKey , A.TargetYear FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d') <= STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(3),'%m%d')),'%Y%m%d') THEN YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')) ELSE YEAR(DATE_ADD(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d'), INTERVAL -1 YEAR )) END AS TargetYear from covi_smart4j.sys_object_user) A INNER JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin'
WHERE B.CompanyCode = #{domainCode}
) B ON A.UR_Code = B.UserCode and A.year = B.TargetYear and A.VacKind='PUBLIC' ) TBL WHERE 1=1
AND DeptName LIKE CONCAT('%', #{schTxt}, '%')
AND DisplayName LIKE CONCAT('%', #{schTxt}, '%')
AND RetireDate = '' AND DeptCode NOT LIKE '%RetireDept'
AND RetireDate != '' AND year(RetireDate) = #{year}
AND UR_CODE = #{urCode}
DeptName DisplayName EnterDate JobPositionName VacDay
ASC DESC
JobPositionSortKey, SortKey ASC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT COUNT(*) AS CNT FROM covi_smart4j.vm_vacationplan A INNER JOIN ( SELECT A.UserCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , A.EnterDate , A.RetireDate , A.SortKey , B.CompanyCode , B.DeptCode , Fn_BaseGetDictionary_S(#{lang},IFNULL(B.DeptName, B.MultiDeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , B.JobPositionSortKey , A.TargetYear FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d') <= STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(3),'%m%d')),'%Y%m%d') THEN YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')) ELSE YEAR(DATE_ADD(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d'), INTERVAL -1 YEAR )) END AS TargetYear from covi_smart4j.sys_object_user) A INNER JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin'
WHERE B.CompanyCode = #{domainCode}
