		  INTO covi_smart4j.vm_vacationplan (UR_Code, YEAR, VacDay
											)
	    VALUES 

        	#{item.urCode}, YEAR(NOW()), #{item.vacDay}

mapper.update:
UPDATE covi_smart4j.vm_vacationplan
   SET LongVacDay = #{vacDay} ,
       LastVacDay = fn_GetLastVacDay(#{urCode}, #{year}, #{domainCode}) ,
       VacDay = #{vacDay} + fn_GetLastVacDay(#{urCode}, #{year}, #{domainCode}) 
 WHERE UR_Code = #{urCode}
   AND YEAR = #{year}

	AND VacKind = #{vacKind}

/* groupware.vacation.updateLastVacDay */
SELECT YEAR(DATE_ADD(STR_TO_DATE(CONCAT(year,'0101'),'%Y%m%d'), INTERVAL 1 YEAR )) as TargetYear ,DATE_FORMAT(DATE_ADD(STR_TO_DATE(UseStartDate,'%Y%m%d'), INTERVAL 1 YEAR ),'%Y%m%d') as UseStartDate ,DATE_FORMAT(DATE_ADD(STR_TO_DATE(UseEndDate,'%Y%m%d'), INTERVAL 1 YEAR ),'%Y%m%d') as UseEndDate ,(SELECT CompanyCode FROM covi_smart4j.sys_object_user_basegroup WHERE UserCode = #{urCode} AND JobType = 'Origin') AS DomainCode ,(select VacDay from covi_smart4j.vm_vacationplan where ur_code = #{urCode} and year = TargetYear and VacKind='PUBLIC') as BeforeVacDay ,(select LongVacDay + fn_GetLastVacDay(#{urCode}, TargetYear , DomainCode) from covi_smart4j.vm_vacationplan where ur_code = #{urCode} and year = TargetYear and VacKind='PUBLIC') as AfterVacDay FROM covi_smart4j.vm_vacationplan WHERE ur_code= #{urCode} AND #{sDate} BETWEEN UseStartDate AND UseEndDate AND VacKind='PUBLIC'
UPDATE covi_smart4j.vm_vacationplan SET LastVacDay = fn_GetLastVacDay(#{urCode}, #{TargetYear}, #{DomainCode}) , VacDay = LongVacDay + fn_GetLastVacDay(#{urCode}, #{TargetYear}, #{DomainCode}) WHERE UR_Code = #{urCode} AND YEAR = #{TargetYear} AND VacKind = 'PUBLIC'

