/* groupware.vacation.selectVacationInfoList */ SELECT VacationInfoID , A.UR_Code , Fn_BaseGetDictionary_S(#{lang}, u.MultiDisplayName) DisplayName , APPDATE , ENDDATE , GUBUN , Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) AS GubunName , VacYear , Sdate , Edate , VacFlag , VacOffFlag , Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) AS VacFlagName , cast(cast(FORMAT(a.VacDay,3) as DECIMAL(9,3)) as float) AS VacDay , Reason , WorkItemID , A.ProcessID , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , (CASE WHEN (GUBUN = 'VACATION_CANCEL' OR GUBUN = 'VACATION_PUBLIC' OR GUBUN = 'VACATION_PUBLIC_CANCEL' ) THEN CONCAT('(', Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName), ')', Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName)) ELSE Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) END ) AS VACTEXT FROM vm_vacationplan p JOIN vm_vacationinfo A ON a.UR_Code = p.UR_Code AND a.Sdate BETWEEN DATE_FORMAT(p.UseStartDate, '%Y-%m-%d') AND DATE_FORMAT(p.UseEndDate , '%Y-%m-%d') LEFT JOIN sys_object_user U ON A.UR_Code = u.UserCode LEFT JOIN sys_object_user_basegroup B ON A.UR_Code = B.UserCode AND JobType = 'Origin' LEFT JOIN sys_base_code C ON A.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID =0 LEFT JOIN sys_base_code D ON A.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} WHERE p.year = #{year}
AND A.VacationInfoID = #{vacationInfoID}
AND B.Companycode = #{domainCode}
AND A.UR_Code = #{urCode}
AND DeptName LIKE CONCAT('%', #{schTxt}, '%')
AND DisplayName LIKE CONCAT('%', #{schTxt}, '%')
VacationInfoID DESC
DisplayName APPDATE ENDDATE VacFlagName VacYear Sdate Edate VacDay Reason GubunName VACTEXT DeptName VacationInfoID
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

