/* groupware.vacation.selectMyVacationInfoList */ SELECT VacationInfoID , APPDATE , ENDDATE , GUBUN , Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) AS GubunName , VacYear , Sdate , Edate , VacFlag , VacOffFlag , cast(cast(FORMAT(e.VacDay,3) as DECIMAL(9,3)) as float) AS VacDay , Reason , WorkItemID , e.ProcessID , IFNULL(AR.ProcessID,0) AS EXIST_APPROVAL_FORM , IFNULL(AR2.ReqSeq,0) AS EXIST_REQUEST_FORM , IFNULL(AR2.ReqTitle,'') AS ReqTitle , (CASE WHEN (GUBUN = 'VACATION_CANCEL' OR GUBUN = 'VACATION_PUBLIC' OR GUBUN = 'VACATION_PUBLIC_CANCEL' ) THEN CONCAT('(', Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName), ')', Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName)) ELSE Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) END ) AS VACTEXT FROM vm_vacationplan A
LEFT JOIN SYS_BASE_CODE k ON k.CodeGroup = 'VACATION_TYPE' AND k.IsUse ='Y' AND k.DomainID = #{domainID}
JOIN vm_vacationinfo e ON a.UR_Code = e.UR_Code AND e.Sdate BETWEEN DATE_FORMAT(a.UseStartDate, '%Y-%m-%d') AND DATE_FORMAT(a.UseEndDate , '%Y-%m-%d')
AND k.Code = e.VacFlag
AND a.VacKind = e.VacFlag
LEFT JOIN covi_smart4j.attendance_request AR ON e.ProcessID = AR.ProcessID LEFT JOIN covi_smart4j.attendance_request AR2 ON e.ProcessID = AR2.ReqSeq LEFT JOIN covi_smart4j.sys_base_code C ON e.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID =0 LEFT JOIN covi_smart4j.sys_base_code D ON e.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} WHERE A.UR_Code = #{urCode} AND a.VacKind = #{VacKind} AND a.UseStartDate = #{UseStartDate}
VacationInfoID DESC
DisplayName APPDATE ENDDATE VacFlagName VacYear Sdate Edate VacDay Reason GubunName VACTEXT DeptName VacationInfoID
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

	/* groupware.vacation.selectMyVacationInfoListCnt */
