SELECT VacationInfoID , A.UR_Code , DisplayName , APPDATE , ENDDATE , GUBUN , Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) AS GubunName , VacYear , Sdate , Edate , VacFlag , VacOffFlag , Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) AS VacFlagName , cast(cast(FORMAT(VacDay,3) as DECIMAL(9,3)) as float) AS VacDay , Reason , WorkItemID , A.ProcessID , IFNULL(AR.ProcessID,0) AS EXIST_APPROVAL_FORM , IFNULL(AR2.ReqSeq,0) AS EXIST_REQUEST_FORM , IFNULL(AR2.ReqTitle,'') AS ReqTitle , DeptName , JobPositionName , (CASE WHEN (GUBUN = 'VACATION_CANCEL' OR GUBUN = 'VACATION_PUBLIC' OR GUBUN = 'VACATION_PUBLIC_CANCEL' ) THEN CONCAT('(', Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName), ')', Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName)) ELSE Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) END ) AS VACTEXT FROM covi_smart4j.vm_vacationinfo A LEFT JOIN ( SELECT A.UserCode , B.CompanyCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, IFNULL(E.DeptName, B.MultiDeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , VacYearRange ,TargetYear FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , (CASE WHEN DATE(DATE_ADD(EnterDate, INTERVAL 12 MONTH)) <= DATE(NOW()) THEN 1 ELSE 0 END ) as IsOneYear ,CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(),'%m%d')),'%Y%m%d') < STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(EnterDate,'%Y-%m-%d'),'%m%d')),'%Y%m%d') THEN STR_TO_DATE(CONCAT(YEAR(DATE_ADD(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d'), INTERVAL -1 YEAR )),DATE_FORMAT(STR_TO_DATE(EnterDate,'%Y-%m-%d'),'-%m-%d')),'%Y-%m-%d') ELSE STR_TO_DATE(CONCAT(YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')),DATE_FORMAT(STR_TO_DATE(EnterDate,'%Y-%m-%d'),'-%m-%d')),'%Y-%m-%d') END AS VacYearRange , CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d') <= STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(3),'%m%d')),'%Y%m%d') THEN YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')) ELSE YEAR(DATE_ADD(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d'), INTERVAL -1 YEAR )) END AS TargetYear from covi_smart4j.sys_object_user) A LEFT JOIN covi_smart4j.sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' LEFT JOIN covi_smart4j.vm_vacationplan E ON A.UserCode = E.UR_Code and E.year = TargetYear and E.VacKind='PUBLIC' ) B ON A.UR_Code = B.UserCode AND STR_TO_DATE(A.Sdate,'%Y-%m-%d') >= B.VacYearRange AND STR_TO_DATE(A.Edate,'%Y-%m-%d') <= DATE_ADD(DATE_ADD(B.VacYearRange, INTERVAL 1 YEAR), INTERVAL -1 DAY) LEFT JOIN covi_smart4j.sys_base_code C ON A.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID =0 LEFT JOIN covi_smart4j.sys_base_code D ON A.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} LEFT JOIN covi_smart4j.attendance_request AR ON A.ProcessID = AR.ProcessID LEFT JOIN covi_smart4j.attendance_request AR2 ON A.ProcessID = AR2.ReqSeq WHERE VacYear = #{year}
AND A.VacationInfoID = #{vacationInfoID}
AND B.Companycode = #{domainCode}
AND A.UR_Code = #{urCode}
AND (GUBUN ='VACATION_PUBLIC' OR GUBUN ='VACATION_PUBLIC_CANCEL')
AND DeptName LIKE CONCAT('%', #{schTxt}, '%')
AND DisplayName LIKE CONCAT('%', #{schTxt}, '%')
VacationInfoID DESC
DisplayName APPDATE ENDDATE VacFlagName VacYear Sdate Edate VacDay Reason GubunName VACTEXT DeptName VacationInfoID
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT COUNT(VacationInfoID) FROM covi_smart4j.vm_vacationinfo A LEFT JOIN ( SELECT A.UserCode , B.CompanyCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, IFNULL(E.DeptName, B.MultiDeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , VacYearRange ,TargetYear FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , (CASE WHEN DATE(DATE_ADD(EnterDate, INTERVAL 12 MONTH)) <= DATE(NOW()) THEN 1 ELSE 0 END ) as IsOneYear ,CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(),'%m%d')),'%Y%m%d') < STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(EnterDate,'%Y-%m-%d'),'%m%d')),'%Y%m%d') THEN STR_TO_DATE(CONCAT(YEAR(DATE_ADD(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d'), INTERVAL -1 YEAR )),DATE_FORMAT(STR_TO_DATE(EnterDate,'%Y-%m-%d'),'-%m-%d')),'%Y-%m-%d') ELSE STR_TO_DATE(CONCAT(YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')),DATE_FORMAT(STR_TO_DATE(EnterDate,'%Y-%m-%d'),'-%m-%d')),'%Y-%m-%d') END AS VacYearRange , CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d') <= STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(3),'%m%d')),'%Y%m%d') THEN YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')) ELSE YEAR(DATE_ADD(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d'), INTERVAL -1 YEAR )) END AS TargetYear from covi_smart4j.sys_object_user) A LEFT JOIN covi_smart4j.sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' LEFT JOIN covi_smart4j.vm_vacationplan E ON A.UserCode = E.UR_Code and E.year = TargetYear and E.VacKind='PUBLIC' ) B ON A.UR_Code = B.UserCode AND STR_TO_DATE(A.Sdate,'%Y-%m-%d') >= B.VacYearRange AND STR_TO_DATE(A.Edate,'%Y-%m-%d') <= DATE_ADD(DATE_ADD(B.VacYearRange, INTERVAL 1 YEAR), INTERVAL -1 DAY) WHERE VacYear = #{year}
AND A.VacationInfoID = #{vacationInfoID}
AND B.Companycode = #{domainCode}
AND UR_Code = #{urCode}
AND (GUBUN ='VACATION_PUBLIC' OR GUBUN ='VACATION_PUBLIC_CANCEL')
AND DeptName LIKE CONCAT('%', #{schTxt}, '%')
AND DisplayName LIKE CONCAT('%', #{schTxt}, '%')

