	(SELECT COUNT(0) FROM covi_smart4j.vm_vacationinfo
	WHERE UR_Code = #{urCode}
	AND SDate = #{sDate}
	AND EDate = #{eDate}
	AND GUBUN = #{applyGubun}) 
