	INSERT 
	  INTO covi_smart4j.vm_vacationinfo (VacYear, UR_Code, 
	  									 UR_Name, 
	  									 VacFlag, Sdate, Edate, VacDay, 
	  									 Reason, APPDATE, ENDDATE, GUBUN
	  						   			)
    VALUES (#{vacYear}, #{urCode}, 
    		(SELECT DisplayName
			   FROM covi_smart4j.sys_object_user
			  WHERE UserCode = #{urCode}
