	(SELECT
			CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d') <= STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(3),'%m%d')),'%Y%m%d') THEN
				CONCAT(YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')),DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d'))
