				DATE_FORMAT(DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')),DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d'), INTERVAL 1 YEAR), INTERVAL -1 DAY),'%Y%m%d')
