AND vacFlag = #{vacFlag}
GROUP BY vm.Ur_Code, ad.dayList HAVING SUM(fn_attend_calVacDay(VacFlag, VacOffFlag, GUBUN))>0 ) b ON A.vacationinfoid = b.vacationinfoid LEFT JOIN covi_smart4j.vm_vacationplan E ON A.UR_Code = E.UR_Code and E.year = a.VacYear LEFT JOIN covi_smart4j.sys_base_code D ON A.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} WHERE 1 =1
VacDate DESC
