LEFT JOIN covi_smart4j.vm_vacationinfo_day F ON A.UR_Code = F.UR_Code AND STR_TO_DATE(F.VacDate,'%Y-%m-%d') between STR_TO_DATE(A.UseStartDate,'%Y%m%d') and STR_TO_DATE(A.UseEndDate,'%Y%m%d') AND F.VacFlag = A.VacKind WHERE A.Year = #{year} AND A.VacKind <> 'PUBLIC'
