mapper.select:
SELECT COUNT(*)
FROM (
		SELECT A.UR_Code
		FROM vm_plan_hist H
		LEFT JOIN vm_vacationplan A ON A.VacKind = H.VacKind AND A.UR_Code = H.UR_Code AND A.UseStartDate = H.UseStartDate AND A.UseEndDate = H.UseEndDate  AND A.YEAR = #{year}
		LEFT JOIN sys_object_user e ON A.UR_Code = e.UserCode
		LEFT JOIN sys_object_user_basegroup B ON e.UserCode = B.UserCode AND JobType = 'Origin'  
		LEFT JOIN sys_base_code C ON A.VacKind = C.Code AND C.CodeGroup = 'VACATION_KIND' AND C.DomainID = 0
	 	LEFT JOIN sys_base_code D ON A.VacKind = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID}
		LEFT JOIN covi_smart4j.vm_vacationinfo F
			ON A.UR_Code = F.UR_Code
				AND STR_TO_DATE(F.Sdate,'%Y-%m-%d') between STR_TO_DATE(A.UseStartDate,'%Y%m%d') and STR_TO_DATE(A.UseEndDate,'%Y%m%d')
				AND F.VacFlag = A.VacKind
		WHERE 1=1

			AND B.Companycode = #{domainCode}


			AND A.VacKind = #{vacKind}


			AND A.UseStartDate = #{sDate}


			AND A.UseEndDate = #{eDate}




					AND e.RetireDate = ''
					AND B.DeptCode NOT LIKE '%RetireDept'


					AND e.RetireDate != ''
					AND year(e.RetireDate) = #{year}






					AND (A.DeptName LIKE CONCAT('%', #{schTxt}, '%') or b.MultiDeptName LIKE CONCAT('%', #{schTxt}, '%'))


					AND DisplayName LIKE CONCAT('%', #{schTxt}, '%')



		GROUP BY A.UR_Code, A.VacKind, A.UseStartDate, A.UseEndDate, H.VacDay
	) T

SELECT A.UR_Code , e.DisplayName , Fn_BaseGetDictionary_S(#{lang}, IFNULL(A.DeptName, B.MultiDeptName)) AS DeptName , B.JobPositionName , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) ELSE Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) END AS ExtVacName , Year as ExtVacYear , H.UseStartDate as ExtSdate , H.UseEndDate as ExtEdate , H.VacKind as ExtVacType , H.VmComment as VmComment , DATE_FORMAT(H.RegistDate, '%Y-%m-%d') as ChangeDate , cast(cast(A.VacDay as DECIMAL(9,3)) as float) AS ExtVacDay , H.VmComment as ExtReason , (SELECT Fn_BaseGetDictionary_S(#{lang}, MultiDisplayName) FROM sys_object_user WHERE UserCode = a.RegisterCode) AS RegisterName , A.RegisterCode , DATE_FORMAT(A.RegistDate, '%Y-%m-%d') AS RegistDate , CONCAT(DATE_FORMAT(A.UseStartDate,'%y.%m.%d'),' ~ ',DATE_FORMAT(A.UseEndDate,'%y.%m.%d')) AS ExpDate , cast(SUM(IFNULL(F.VacDay,0.0)) as float) AS ExtUseVacDay , cast(A.VacDay-SUM(IFNULL(F.VacDay,0.0)) as float) AS ExtRemainVacDay FROM vm_plan_hist H LEFT JOIN vm_vacationplan A ON A.VacKind = H.VacKind AND A.UR_Code = H.UR_Code AND A.UseStartDate = H.UseStartDate AND A.UseEndDate = H.UseEndDate AND A.YEAR = #{year} LEFT JOIN sys_object_user e ON A.UR_Code = e.UserCode LEFT JOIN sys_object_user_basegroup B ON e.UserCode = B.UserCode AND JobType = 'Origin' LEFT JOIN sys_base_code C ON A.VacKind = C.Code AND C.CodeGroup = 'VACATION_KIND' AND C.DomainID = 0 LEFT JOIN sys_base_code D ON A.VacKind = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} LEFT JOIN covi_smart4j.vm_vacationinfo F ON A.UR_Code = F.UR_Code AND STR_TO_DATE(F.Sdate,'%Y-%m-%d') between STR_TO_DATE(A.UseStartDate,'%Y%m%d') and STR_TO_DATE(A.UseEndDate,'%Y%m%d') AND F.VacFlag = A.VacKind WHERE 1=1
AND B.Companycode = #{domainCode}
