AND B.DisplayName LIKE CONCAT('%', #{schTxt}, '%')

SELECT A.VacationInfoID , A.UR_Code , B.DisplayName , A.APPDATE , A.ENDDATE , A.GUBUN , Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) AS GubunName , A.VacYear , A.Sdate , A.Edate , A.VacFlag , A.VacOffFlag , Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) AS VacFlagName , cast(cast(FORMAT(A.VacDay,3) as DECIMAL(9,3)) as float) AS VacDay , cast(IFNULL(B.VacDayTot,0.0) as float) AS VacDayTot , cast(cast(FORMAT(IFNULL(SUM(V.VacDayUse),0.0),3) as DECIMAL(9,3)) as float) AS VacDayUse , cast(cast(FORMAT(IFNULL(B.VacDayTot-SUM(V.VacDayUse),0.0),3) as DECIMAL(9,3)) as float) AS VacDayRemain , A.Reason , A.WorkItemID , A.ProcessID , DeptName , JobPositionName , (CASE WHEN (A.GUBUN = 'VACATION_CANCEL' OR A.GUBUN = 'VACATION_PUBLIC' OR A.GUBUN = 'VACATION_PUBLIC_CANCEL' ) THEN CONCAT('(', Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName), ')', Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName)) ELSE Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) END ) AS VACTEXT FROM (select * from covi_smart4j.vm_vacationinfo where VacYear = #{year}) A LEFT JOIN ( SELECT A.UserCode , B.CompanyCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, IFNULL(E.DeptName, B.MultiDeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , SUM(VacDay) AS VacDayTot , A.Sdate, A.Edate FROM ( select UserCode, DisplayName, MULTIDISPLAYNAME, ENTERDATE ,CASE WHEN CREATEMETHOD = 'J' THEN STR_TO_DATE(CONCAT(TargetYear,DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'m%d')),'YYYYMMDD') ELSE STR_TO_DATE(CONCAT(TargetYear, '0101'),'%Y%m%d') END as Sdate ,CASE WHEN CREATEMETHOD = 'J' THEN DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(TargetYear,DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'m%d')),'YYYYMMDD'),INTERVAL 1 YEAR),INTERVAL -1 DAY) ELSE STR_TO_DATE(CONCAT(TargetYear, '1231'),'%Y%m%d') END as Edate from ( select UserCode, DisplayName, MULTIDISPLAYNAME, ENTERDATE, CFG.CREATEMETHOD ,CASE WHEN CFG.CREATEMETHOD = 'J' THEN CASE WHEN STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d') <= STR_TO_DATE(CONCAT(#{year},DATE_FORMAT(NOW(3),'%m%d')),'%Y%m%d') THEN YEAR(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d')) ELSE YEAR(DATE_ADD(STR_TO_DATE(CONCAT(#{year},'0101'),'%Y%m%d'), INTERVAL -1 YEAR)) END ELSE #{year} END AS TargetYear from SYS_OBJECT_USER left join VM_CONFIG CFG ON CFG.COMPANYCODE = #{CompanyCode}) a ) A LEFT JOIN covi_smart4j.sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' LEFT JOIN covi_smart4j.vm_vacationplan E ON A.UserCode = E.UR_Code and STR_TO_DATE(E.USESTARTDATE,'%Y%m%d') >= A.Sdate and STR_TO_DATE(E.USEENDDATE,'%Y%m%d') <= A.Edate group by A.UserCode, B.CompanyCode, DisplayName, JobPositionName, A.MultiDisplayName, B.MultiDeptName, B.MultiJobPositionName, A.SDATE, A.EDATE ) B ON A.UR_Code = B.UserCode LEFT JOIN covi_smart4j.sys_base_code C ON A.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID = 0 LEFT JOIN covi_smart4j.sys_base_code D ON A.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} LEFT JOIN (select UR_Code, VacDay as VacDayUse, Sdate, Edate from covi_smart4j.vm_vacationinfo) V ON V.UR_Code = A.UR_Code AND STR_TO_DATE(V.Sdate,'%Y-%m-%d') >= B.Sdate AND STR_TO_DATE(V.Edate,'%Y-%m-%d') <= B.Edate WHERE A.VacationInfoID in
#{item}
AND B.Companycode = #{domainCode}
group by A.VacationInfoID, A.UR_Code, B.DisplayName, A.APPDATE , A.ENDDATE , A.GUBUN, C.MULTICODENAME , A.VacYear , A.Sdate , A.Edate , A.VacFlag , A.VacOffFlag , D.MultiCodeName, A.Reason , A.WorkItemID , A.ProcessID , B.DeptName, JobPositionName
A.VacationInfoID DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT COUNT(*) FROM (select * from covi_smart4j.vm_vacationinfo where VacYear = #{year} AND VacationInfoID in
#{item}
GROUP BY UR_Code) A LEFT JOIN ( SELECT A.UserCode , B.CompanyCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName FROM covi_smart4j.sys_object_user A LEFT JOIN covi_smart4j.sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin'
AND B.Companycode = #{domainCode}
) B ON A.UR_Code = B.UserCode

/* groupware.vacation.selectVacPromotionDate */
	SELECT
		CompanyCode, ReqType, ReqOrd
		, CAST(CAST(ReqMonth AS DECIMAL(9,3)) AS FLOAT) AS ReqMonth
		, ReqTermDay, ReqOrder
	FROM covi_smart4j.vm_reqconf
	WHERE CompanyCode = #{CompanyCode}
		AND ReqType = #{reqType}

mapper.update:
/* groupware.vacation.updateVacPromotionDate */
	INSERT INTO covi_smart4j.vm_reqconf
	      (  CompanyCode, ReqType, ReqOrd, ReqMonth, ReqTermDay, ReqOrder )
	VALUES ( #{CompanyCode}, #{reqType}, #{reqOrd}, #{reqMonth}, #{reqTermDay}, #{reqOrder} )
    ON   DUPLICATE KEY
	UPDATE 	 ReqMonth = #{reqMonth}
			,reqTermDay = #{reqTermDay}

mapper.delete:
/* groupware.vacation.deleteVacationPlanHist */
delete from covi_smart4j.vm_plan_hist
WHERE VacKind = 'PUBLIC'
AND UR_Code in (
	SELECT A.UserCode
	FROM covi_smart4j.sys_object_user A
	LEFT JOIN (SELECT UserCode, CompanyName, CompanyCode, DeptCode, MultiDeptName
	FROM covi_smart4j.sys_object_user_basegroup
	WHERE JobType = 'Origin'
	) B ON A.UserCode = B.UserCode
	WHERE B.CompanyCode = #{CompanyCode}
)

mapper.select:
/* groupware.vacation.selectAutoIncreaseMonthlyVacationV2 */
SELECT U.UserCode, U.DeptCode, U.DeptName,U.YEAR, (IFNULL(V.VacDay,0.0)+1) as VacDay, U.VacKind, U.UseStartDate, U.UseEndDate, CONCAT('[] ', NOW(), ' 1 ') as VmComment FROM ( SELECT a.UserCode, b.DeptCode, b.DeptName, TARGETYEAR AS YEAR, 0 AS VacDay, 'PUBLIC' AS VacKind , CONCAT(TARGETYEAR,DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')) AS UseStartDate , DATE_FORMAT(DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(TARGETYEAR,DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d'),INTERVAL 1 YEAR),INTERVAL -1 DAY),'%Y%m%d') AS UseEndDate
FROM (select UserCode, IsUse, IsHR, IsDisplay, EnterDate , CASE WHEN STR_TO_DATE(CONCAT(YEAR(NOW()),DATE_FORMAT(STR_TO_DATE(REPLACE(EnterDate,'-',''),'%Y%m%d'),'%m%d')),'%Y%m%d') <= NOW(3) THEN YEAR(NOW()) ELSE YEAR(DATE_ADD(NOW(), INTERVAL -1 YEAR )) END AS TARGETYEAR from covi_smart4j.sys_object_user where IsUse = 'Y' and EnterDate IS NOT NULL and EnterDate != '' AND STR_TO_DATE(REPLACE(EnterDate, '-', ''), '%Y%m%d') > DATE_SUB(NOW(), INTERVAL 1 YEAR) AND TIMESTAMPDIFF(MONTH, DATE_FORMAT(EnterDate, '%Y-%m-01'), DATE_FORMAT(NOW(), '%Y-%m-01')) > 0 # AND (DAY(EnterDate) = DAY(NOW()) # (Day) # OR (date_format(NOW(), '%Y-%m-%d') = LAST_DAY(NOW()) AND DAY(EnterDate) > DAY(LAST_DAY(NOW()))) ) ) a INNER JOIN covi_smart4j.sys_object_user_basegroup b ON a.usercode = b.usercode AND b.JobType = 'Origin' and b.CompanyCode = #{domainCode} ) U LEFT JOIN covi_smart4j.vm_vacationplan V ON U.UserCode = V.UR_Code AND V.VacKind = 'PUBLIC' AND V.UseStartDate >= U.UseStartDate AND V.UseEndDate <= U.UseEndDate

mapper.update:
/* groupware.vacation.updateAutoIncreaseMonthlyVacationV2 */

UPDATE covi_smart4j.vm_vacationplan
SET VacDay = #{vacDay}
, LongVacDay = #{vacDay}-LastVacDay
WHERE UR_Code = #{urCode}
AND  VacKind = #{vacKind}
AND  UseStartDate = #{useStartDate}
AND  UseEndDate = #{useEndDate}

	/* groupware.vacation.insertAutoCreateAnnualVacationV2 */

     UPDATE  vm_vacationplan
      SET    LongVacDay = #{longVacDay},
      		 LastVacDay = #{lastVacDay},
      		 VacDay = #{longVacDay}+#{lastVacDay},
      		 UseStartDate = #{useStartDate},
      		 UseEndDate = #{useEndDate},
	     	 VmComment = #{comment}
     WHERE  UR_Code = #{urCode}
	  AND   YEAR = #{year}
	  AND   VacKind = 'PUBLIC'

mapper.select:
/* legacy.formCmmFunction.selectVacationInfo */ SELECT SUM(EXIST) AS cnt FROM (SELECT CASE WHEN #{VacOffFlag}='AM' OR #{VacOffFlag}='PM' THEN CASE WHEN COUNT(1) = 0 THEN 0 WHEN LENGTH(#{STime}) > 0 AND LENGTH(#{ETime}) > 0 THEN CASE WHEN (CAST(REPLACE(#{STime},':','') AS INTEGER)+1 >= CAST(REPLACE(StartTime,':','') AS INTEGER) AND CAST(REPLACE(#{STime},':','') AS INTEGER)+1 <= CAST(REPLACE(EndTime,':','') AS INTEGER)) OR (CAST(REPLACE(#{ETime},':','') AS INTEGER)-1 >= CAST(REPLACE(StartTime,':','') AS INTEGER) AND CAST(REPLACE(#{ETime},':','') AS INTEGER)-1 <= CAST(REPLACE(EndTime,':','') AS INTEGER)) THEN 1 ELSE 0 END WHEN LENGTH(#{STime}) = 0 AND LENGTH(#{ETime}) = 0 THEN CASE WHEN LENGTH(StartTime) > 0 AND LENGTH(EndTime) > 0 AND VacOffFlag=#{VacOffFlag} THEN 1 WHEN LENGTH(StartTime) = 0 AND LENGTH(EndTime) = 0 AND VacOffFlag=#{VacOffFlag} THEN 1 WHEN VacFlag='VACATION_ANNUAL' THEN 1 ELSE 0 END ELSE 1 END ELSE COUNT(1) END AS EXIST FROM covi_smart4j.vm_vacationinfo_day WHERE STR_TO_DATE(VacDate,'%Y-%m-%d') BETWEEN STR_TO_DATE(#{StartDate},'%Y-%m-%d') AND STR_TO_DATE(#{EndDate},'%Y-%m-%d') AND UR_Code = #{UserCode} AND (GUBUN != 'VACATION_CANCEL' and GUBUN != 'VACATION_PUBLIC_CANCEL')
AND (VacOffFlag is null OR VacOffFlag = '0' OR VacOffFlag = #{VacOffFlag})
) T

    SELECT COUNT(1) - (DATEDIFF(STR_TO_DATE(#{EndDate},'%Y-%m-%d') , STR_TO_DATE(#{StartDate},'%Y-%m-%d')) + 1) AS invalidcnt
    FROM covi_smart4j.vm_vacationinfo_day
	WHERE UR_Code = #{UserCode}
	AND VacFlag = #{VacFlag}
	AND VacOffFlag = #{VacOffFlag}
	AND STR_TO_DATE(VacDate,'%Y-%m-%d') BETWEEN STR_TO_DATE(#{StartDate},'%Y-%m-%d') AND STR_TO_DATE(#{EndDate},'%Y-%m-%d')
	AND (GUBUN != 'VACATION_CANCEL' and GUBUN != 'VACATION_PUBLIC_CANCEL')

/* groupware.vacation.getVacationPlanHist */ SELECT UR_Code , (SELECT Fn_BaseGetDictionary_S(#{lang}, MultiDisplayName) FROM covi_smart4j.sys_object_user WHERE UserCode = UR_Code) AS DisplayName , CONCAT(DATE_FORMAT(UseStartDate,'%y.%m.%d'),' ~ ',DATE_FORMAT(UseEndDate,'%y.%m.%d')) AS UseDate , cast(cast(VacDay as DECIMAL(9,3)) as float) AS VacDay , VmComment as Comment , DATE_FORMAT(RegistDate, '%Y-%m-%d') as ChangeDate , VmMethod FROM covi_smart4j.vm_plan_hist WHERE UR_Code = #{urCode} AND UseStartDate = DATE_FORMAT(#{startDate}, '%Y%m%d') AND UseEndDate = DATE_FORMAT(#{endDate}, '%Y%m%d') AND Vackind = 'PUBLIC'
RegistDate DESC
DisplayName UseDate VacDay Comment ChangeDate VmMethod RegistDate
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

mapper.update:
/* groupware.vacation.updateLastLongVacDay */
	UPDATE covi_smart4j.vm_vacationplan
	   SET LastVacDay = fn_GetLastVacDay(#{userCode}, #{year}, #{domainCode}) , 
	       LongVacDay = (VacDay-fn_GetLastVacDay(#{userCode}, #{year}, #{domainCode}))
	 WHERE UR_Code = #{userCode}
	   AND YEAR = #{year}
		AND VacKind = 'PUBLIC'

mapper.select:
	/* groupware.vacation.selectVacDay */
       SELECT IF(IFNULL(Reserved3, 1) = '', 1, IFNULL(Reserved3, 1)) as vacday
       FROM covi_smart4j.sys_base_code 
       WHERE DomainID = (SELECT DomainID FROM covi_smart4j.sys_object_domain WHERE DomainCode = #{CompanyCode}) 
       AND CodeGroup = 'VACATION_TYPE' AND Code = #{vacFlag}
