SELECT FD.MenuID , FD.FolderID , FD.OwnerCode AS FolderOwnerCode , FD.FolderType , BM.Version , BM.MessageID , BM.Subject , BM.CreatorCode , IFNULL(BM.OwnerCode, '') AS OwnerCode , IFNULL(BM.RegistDept, '') AS RegistDept , IFNULL(BM.UseMessageReadAuth, 'N') AS UseMessageReadAuth , (SELECT IFNULL(UseMessageAuth,'N') FROM board_config WHERE FolderID = BM.FolderID) AS UseMessageAuth FROM covi_smart4j.board_message AS BM INNER JOIN covi_smart4j.sys_object_folder AS FD ON BM.FolderID = FD.FolderID WHERE FD.IsUse = 'Y' AND FD.IsDisplay = 'Y' AND FD.DeleteDate IS NULL AND BM.DeleteDate IS NULL AND BM.IsPopup = 'Y' AND BM.MsgType = 'O' AND BM.MsgState IN ('C', 'A') AND #{localCurrentDate} between BM.PopupStartDate AND BM.PopupEndDate
