/* webpart.board.selectEmployeesNoticeCount */ SELECT COUNT(*) AS Cnt FROM ( SELECT Fn_BaseGetDictionary_S(#{lang}, ur.MultiDisplayName) AS UserName , Fn_BaseGetDictionary_S(#{lang}, gr.MultiJobLevelName ) AS JobLevelName , Fn_BaseGetDictionary_S(#{lang}, gr.MultiJobTitleName ) AS JobTitleName , Fn_BaseGetDictionary_S(#{lang}, gr.MultiJobPositionName ) AS JobPositionName , ur.MailAddress AS MailAddress , ur.PhotoPath , notice.* FROM (
UNION ALL ( SELECT a.UserCode , 'Birth' AS Type , a.BirthDiv AS DateDiv , DATE_FORMAT(a.BirthDate, '%Y-%m-%d') AS Date FROM sys_object_user AS a WHERE
(a.BirthDiv = 'S' and DATE_FORMAT(a.BirthDate, '%m%d') = DATE_FORMAT(#{localCurrentDate}, '%m%d')) OR (a.BirthDiv = 'L' AND DATE_FORMAT(a.BirthDate, '%m%d') = ( SELECT DATE_FORMAT(LunarDate, '%m%d') FROM SYS_CALENDAR WHERE SOLARDATE = DATE_FORMAT(#{localCurrentDate}, '%Y-%m-%d') AND DN_ID = 0 ))
(a.BirthDiv = 'S' and DATE_FORMAT(a.BirthDate, '%m') = DATE_FORMAT(#{localCurrentDate}, '%m')) OR (a.BirthDiv = 'L' AND DATE_FORMAT(a.BirthDate, '%m') = ( SELECT DATE_FORMAT(LunarDate, '%m') FROM SYS_CALENDAR WHERE SOLARDATE = DATE_FORMAT(#{localCurrentDate}, '%Y-%m') AND DN_ID = 0 ))
)
UNION ALL (
SELECT a.UserCode , 'Enter' AS Type , 'S' AS DateDiv , DATE_FORMAT(a.EnterDate, '%Y-%m-%d') AS Date FROM sys_object_user AS a WHERE a.EnterDate IS NOT NULL AND a.EnterDate != '' AND a.EnterDate > (#{localCurrentDate} - INTERVAL #{enterInterval} day)
)
UNION ALL ( SELECT EventOwner AS UserCode , EventType AS Type , 'S' AS DateDiv , DATE_FORMAT(EventDate, '%Y-%m-%d') AS Date FROM board_message_event WHERE EventDate IS NOT NULL AND EventDate BETWEEN DATE_FORMAT(#{localCurrentDate},'%Y-%m-%d') AND DATE_FORMAT(date_add(#{localCurrentDate},interval 1+#{addinterval} day),'%Y-%m-%d') ORDER BY EventType, RegistDate DESC )
UNION ALL (
SELECT Apply.UR_Code as UserCode, Apply.Type, Apply.DateDiv, Apply.Date FROM ( SELECT a.UR_Code, a.VacFlag AS Type, 'S' AS DateDiv, b.VacDate as Date, b.VacDay FROM covi_smart4j.vm_vacationinfo AS a JOIN covi_smart4j.vm_vacationinfo_day b on (b.UR_Code = a.UR_Code and a.VacationInfoID = b.VacationInfoID) WHERE a.GUBUN = 'VACATION_APPLY' AND b.vacdate BETWEEN DATE_FORMAT(#{localCurrentDate},'%Y-%m-%d') AND DATE_FORMAT(date_add(#{localCurrentDate},interval 1+#{addinterval} day),'%Y-%m-%d') ) AS Apply
)
( SELECT a.UserCode , 'Birth' AS Type , a.BirthDiv AS DateDiv , DATE_FORMAT(#{localCurrentDate}, '%Y-%m-%d') AS Date FROM sys_object_user AS a WHERE
(a.BirthDiv = 'S' and DATE_FORMAT(a.BirthDate, '%m%d') = DATE_FORMAT(#{localCurrentDate}, '%m%d')) OR (a.BirthDiv = 'L' AND DATE_FORMAT(a.BirthDate, '%m%d') = ( SELECT DATE_FORMAT(LunarDate, '%m%d') FROM SYS_CALENDAR WHERE SOLARDATE = DATE_FORMAT(#{localCurrentDate}, '%Y-%m-%d') AND DN_ID = 0 ))
(a.BirthDiv = 'S' and DATE_FORMAT(a.BirthDate, '%m') = DATE_FORMAT(#{localCurrentDate}, '%m')) OR (a.BirthDiv = 'L' AND DATE_FORMAT(a.BirthDate, '%m') = ( SELECT DATE_FORMAT(LunarDate, '%m') FROM SYS_CALENDAR WHERE SOLARDATE = DATE_FORMAT(#{localCurrentDate}, '%Y-%m') AND DN_ID = 0 ))
)
(
SELECT a.UserCode , 'Enter' AS Type , 'S' AS DateDiv , DATE_FORMAT(a.EnterDate, '%Y-%m-%d') AS Date FROM sys_object_user AS a WHERE a.EnterDate IS NOT NULL AND a.EnterDate != '' AND a.EnterDate > (#{localCurrentDate} - INTERVAL #{enterInterval} day)
)
( SELECT EventOwner AS UserCode , EventType AS Type , 'S' AS DateDiv , DATE_FORMAT(EventDate, '%Y-%m-%d') AS Date FROM board_message_event WHERE EventDate IS NOT NULL AND EventDate BETWEEN DATE_FORMAT(#{localCurrentDate},'%Y-%m-%d') AND DATE_FORMAT(date_add(#{localCurrentDate},interval 1+#{addinterval} day),'%Y-%m-%d') ORDER BY EventType, RegistDate DESC )
(
SELECT Apply.UR_Code as UserCode, Apply.Type, Apply.DateDiv, Apply.Date FROM ( SELECT a.UR_Code, a.VacFlag AS Type, 'S' AS DateDiv, b.VacDate as Date, b.VacDay FROM covi_smart4j.vm_vacationinfo AS a JOIN covi_smart4j.vm_vacationinfo_day b on (b.UR_Code = a.UR_Code and a.VacationInfoID = b.VacationInfoID) WHERE a.GUBUN = 'VACATION_APPLY' AND b.vacdate BETWEEN DATE_FORMAT(#{localCurrentDate},'%Y-%m-%d') AND DATE_FORMAT(date_add(#{localCurrentDate},interval 1+#{addinterval} day),'%Y-%m-%d') ) AS Apply
)
) AS notice INNER JOIN sys_object_user AS ur ON notice.UserCode = ur.UserCode INNER JOIN sys_object_user_basegroup AS gr ON notice.UserCode = gr.UserCode AND gr.JobType = 'Origin' WHERE ur.IsUse = 'Y' AND ur.IsDisplay = 'Y' AND gr.CompanyCode = #{DN_Code} )a
