mapper.update:
        UPDATE TASK 
		SET OwnerCode = #{targetUserID}
		WHERE OwnerCode = #{sourceUserID}

        UPDATE TASK_FOLDER
		SET OwnerCode = #{targetUserID}
		WHERE OwnerCode = #{sourceUserID}

mapper.select:
SELECT DISTINCT FolderID AS "FolderID" , DisplayName AS "DisplayName" , FolderState AS "FolderState" , FolderStateCode AS "FolderStateCode" , IsShare AS "IsShare" , OwnerCode AS "OwnerCode" , RegisterCode AS "RegisterCode" , ParentFolderID AS "ParentFolderID" , RegisterName AS "RegisterName" , ROWNUM , RegistDate AS "RegistDate" FROM ( SELECT a.FolderID , a.DisplayName , a.State AS FolderState , a.State AS FolderStateCode , a.IsShare , a.OwnerCode , a.RegisterCode , NVL(a.ParentFolderID, 0) AS ParentFolderID , FN_BASEGETDICTIONARY_S(#{lang}, c.MultiDisplayName) AS RegisterName , TO_CHAR(a.RegistDate, 'YYYY-MM-DD HH24:MI:SS') AS RegistDate FROM TASK_FOLDER a LEFT JOIN SYS_OBJECT_USER c ON a.RegisterCode = c.UserCode WHERE NVL(a.ParentFolderID, 0) = #{FolderID} AND a.DeleteDate IS NULL AND a.OwnerCode = #{userID}
AND a.State = #{stateCode}
AND a.DisplayName LIKE '%'||#{searchWord}||'%'
AND c.DisplayName LIKE '%'||#{searchWord}||'%'
) temp
DisplayName FolderState RegisterName FolderID RegistDate
ASC DESC


      		SELECT DISTINCT COUNT(*) 
			FROM TASK_FOLDER a
			LEFT JOIN SYS_OBJECT_USER c ON a.RegisterCode = c.UserCode
			WHERE NVL(a.ParentFolderID, 0)  = #{FolderID}
			AND a.DeleteDate IS NULL
        	AND a.OwnerCode = #{userID}


			 	AND a.State = #{stateCode} 




			      AND a.DisplayName LIKE '%'||#{searchWord}||'%'


			     AND  c.DisplayName LIKE '%'||#{searchWord}||'%'

SELECT TaskID AS "TaskID" , FolderID AS "FolderID" , Subject AS "Subject" , StartDate AS "StartDate" , EndDate AS "EndDate" , TaskState AS "TaskState" , TaskStateCode AS "TaskStateCode" , IsDelay AS "IsDelay" , RegisterCode AS "RegisterCode" , OwnerCode AS "OwnerCode" , RegisterName AS "RegisterName" , RegistDate AS "RegistDate" , ROWNUM FROM ( SELECT a.TaskID , a.FolderID , a.Subject , NVL(a.StartDate, '') AS StartDate , NVL(a.EndDate, '') AS EndDate , a.State AS TaskState , a.State AS TaskStateCode , CASE WHEN (a.State = 'Waiting' AND (a.StartDate < TO_DATE(#{localCurrentDate},'YYYY-MM-DD HH24:MI:SS') OR a.EndDate < TO_DATE(#{localCurrentDate},'YYYY-MM-DD HH24:MI:SS'))) OR (a.State = 'Process' AND a.EndDate < TO_DATE(#{localCurrentDate},'YYYY-MM-DD HH24:MI:SS')) THEN 'Y' ELSE 'N' END AS IsDelay , a.RegisterCode , a.OwnerCode , FN_BASEGETDICTIONARY_S(#{lang}, c.MultiDisplayName) AS RegisterName , TO_CHAR(a.RegistDate, 'YYYY-MM-DD HH24:MI:SS') AS RegistDate FROM TASK a LEFT JOIN SYS_OBJECT_USER c ON a.RegisterCode = c.UserCode WHERE a.FolderID = #{FolderID} AND a.OwnerCode = #{userID} AND a.DeleteDate IS NULL
AND a.OwnerCode = #{userID}
AND a.State = #{stateCode}
AND a.Subject LIKE '%'||#{searchWord}||'%'
AND c.DisplayName LIKE '%'||#{searchWord}||'%'
) A
TaskID Subject TaskState StartDate EndDate IsDelay RegisterName RegistDate
ASC DESC


      		SELECT  COUNT(*)
			FROM TASK a
			LEFT JOIN SYS_OBJECT_USER c ON a.RegisterCode = c.UserCode

			WHERE a.FolderID = #{FolderID}
			AND a.DeleteDate IS NULL
		    AND a.OwnerCode =  #{userID}

			 	AND a.State = #{stateCode} 




				      AND a.Subject LIKE '%'||#{searchWord}||'%'


				     AND  c.DisplayName LIKE '%'||#{searchWord}||'%'
