mapper.select:
SELECT COUNT(*) FROM ATTEND_REQUEST AR JOIN SYS_OBJECT_USER_BASEGROUP BG ON AR.UR_CODE = BG.USERCODE AND BG.JOBTYPE = 'Origin' JOIN SYS_OBJECT_GROUP OG ON BG.COMPANYCODE = OG.COMPANYCODE AND OG.GROUPTYPE='Dept' AND BG.DEPTCODE = OG.GROUPCODE AND MEMBEROF != 'NOUSE' LEFT JOIN SYS_OBJECT_USER UR ON UR.USERCODE = AR.UR_CODE WHERE REQDATE BETWEEN TO_DATE(#{StartDate}||'000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#{EndDate}||'235959', 'YYYYMMDDHH24MISS')
AND OG.GROUPPATH LIKE #{DeptCode}||'%'
AND AR.UR_CODE = #{UserCode} AND BG.JOBTYPE = 'Origin'
AND UR.DISPLAYNAME LIKE '%'||#{searchText}||'%'
AND ar.ReqType = #{ReqType}
AND AR.REQSTATUS = #{ReqStatus}
AND AR.REQMETHOD != #{ExReqMethod}

/* attend.req_mng.getRequestList */
SELECT REQSEQ AS "ReqSeq" , REQTYPE AS "ReqType" , AR.UR_CODE AS "UR_Code" , FN_BASEGETDICTIONARY_S(#{lang}, UR.MULTIDISPLAYNAME) AS "URName" , REQTITLE AS "ReqTitle" , "Comment" AS "Comment" , REQSTATUS AS "ReqStatus" , TO_CHAR(REQDATE ,'YYYY-MM-DD HH24:MI') AS "ReqDate" , Fn_GetBaseCodeName('0', 'AttendReqType', AR.ReqType) AS "ReqName" , Fn_GetBaseCodeName('0', 'AttendReqGubun', AR.ReqGubun) AS "ReqGubunName" , Fn_GetBaseCodeName('0', 'AttendState', AR.ReqStatus) AS "StatusName" , REQMETHOD AS "ReqMethod" , PROCESSID AS "ProcessID" , ar.ReqData as "ReqData" , aj.WorkPointX as "WorkPointX" , aj.WorkPointY as "WorkPointY" , BG.JobPositionSortKey as"JobPositionSortKey" , BG.JobLevelSortKey as"JobLevelSortKey" , BG.JobTitleSortKey as"JobTitleSortKey" FROM ATTEND_REQUEST AR JOIN SYS_OBJECT_USER_BASEGROUP BG ON AR.UR_CODE = BG.USERCODE AND BG.JOBTYPE = 'Origin' JOIN SYS_OBJECT_GROUP OG ON BG.COMPANYCODE = OG.COMPANYCODE AND OG.GROUPTYPE='Dept' AND BG.DEPTCODE = OG.GROUPCODE AND MEMBEROF != 'NOUSE' LEFT JOIN SYS_OBJECT_USER UR ON UR.USERCODE = AR.UR_CODE LEFT JOIN attend_job aj ON aj.JobDate = to_date(substr(to_char(ReqDate ,'yyyymmddhh24miss'),0,8),'yyyymmdd') AND ar.UR_Code = aj.UserCode WHERE REQDATE BETWEEN TO_DATE(#{StartDate}||'000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#{EndDate}||'235959', 'YYYYMMDDHH24MISS')
AND OG.GROUPPATH LIKE #{DeptCode}||'%'
AND AR.UR_CODE = #{UserCode} AND BG.JOBTYPE = 'Origin'
AND UR.DISPLAYNAME LIKE '%'||#{searchText}||'%'
AND ar.ReqType = #{ReqType}
AND AR.REQSTATUS = #{ReqStatus}
AND AR.REQMETHOD != #{ExReqMethod}
"JobTitleSortKey" ASC
"JobLevelSortKey" ASC
"JobPositionSortKey" ASC
"URName" ASC
, REQDATE DESC

mapper.update:

UPDATE ATTEND_REQUEST
SET
	REQSTATUS = #{ReqStatus}
	, APPROVALCODE = #{ApprovalCode}
	, APPROVALDATE = SYSDATE
	, APPROVALCOMMENT = #{ApprovalComment}
WHERE REQSEQ = #{ReqSeq}
