mapper.select:
/* collab.common.getUserProject */
SELECT PrjSeq as "PrjSeq" , PrjName as "PrjName" , PrjStatus as "PrjStatus" , ProgRate as "ProgRate" , IsClose as "IsClose" , TO_Char(CloseDate,'YYYY-MM-DD HH24:MI:SS') as "CloseDate" , PrjColor as "PrjColor" , Remark as "Remark" , StartDate as "StartDate" , EndDate as "EndDate" , RegisterCode as "RegisterCode" , TO_Char(RegisteDate ,'YYYY-MM-DD HH24:MI:SS') as "RegisteDate" ,(SELECT LISTAGG(ctm.UserCode || '^' || Fn_BaseGetDictionary_S(#{lang},sou.MultiDisplayName) || '^' || sou.PhotoPath, '|') WITHIN GROUP(ORDER BY ctm.UserCode) FROM COLLAB_PROJECT_MANAGER ctm JOIN SYS_OBJECT_USER sou on ctm.userCode = sou.userCode WHERE ctm.PrjSeq = p.PrjSeq) as "MmUser" ,(SELECT Fn_BaseGetDictionary_S(#{lang},sou.MultiDisplayName) FROM COLLAB_PROJECT_MEMBER ctm JOIN SYS_OBJECT_USER sou on ctm.userCode = sou.userCode WHERE ctm.PrjSeq = p.PrjSeq AND ROWNUM = 1) as "TmUser" ,(SELECT count(ctm.UserCode) FROM COLLAB_PROJECT_MEMBER ctm JOIN SYS_OBJECT_USER sou on ctm.userCode = sou.userCode WHERE ctm.PrjSeq = p.PrjSeq) as "TmUserCnt" ,IsMember as "IsMember" ,IsManager as "IsManager" ,IsRegister as "IsRegister" ,IsViewer as "IsViewer" ,IsFav as "IsFav" FROM ( SELECT distinct cp.PrjSeq, cp.PrjName, cp.PrjStatus, cp.ProgRate, cp.IsClose, cp.CloseDate, cp.PrjColor, cp.Remark, cp.StartDate, cp.EndDate, cp.RegisterCode, cp.RegisteDate , case when cpm.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsMember , case when cpn.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsManager , case when cpr.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsRegister , case when cpv.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsViewer , NVL((SELECT COUNT(*) FROM COLLAB_PROJECT_FAVORITE WHERE cp.PrjSeq = PrjSeq AND UserCode = #{USERID}),0) IsFav FROM COLLAB_PROJECT cp LEFT JOIN COLLAB_PROJECT_MEMBER cpm ON cp.PrjSeq = cpm.PrjSeq AND cpm.UserCode = #{USERID} LEFT JOIN COLLAB_PROJECT_MANAGER cpn ON cp.PrjSeq = cpn.PrjSeq AND cpn.UserCode = #{USERID} LEFT JOIN COLLAB_PROJECT cpr ON cp.PrjSeq = cpr.PrjSeq AND cpr.RegisterCode = #{USERID} LEFT JOIN COLLAB_PROJECT_VIEWER cpv ON cp.PrjSeq = cpv.PrjSeq AND cpv.UserCode = #{USERID} WHERE 1=1
AND cp.CompanyCode = #{CompanyCode}
AND cp.IsClose='N'
AND cp.IsClose= #{inClose}
AND (cpm.PrjSeq IS NOT NULL OR cpn.PrjSeq IS NOT NULL OR cpr.PrjSeq IS NOT NULL )
AND (cpm.PrjSeq IS NOT NULL )
AND (cp.PrjName like '%' || #{seValue} || '%' or cp.Remark like '%' || #{seValue} || '%')
AND cp.PrjStatus in
#{item}
ORDER BY RegisteDate desc
ORDER BY PrjName asc
) p

/* collab.common.getUserProjectCnt */



	SELECT COUNT(PrjSeq)
	FROM   (
		SELECT DISTINCT cp.PrjSeq, cp.PrjName, cp.PrjStatus, cp.ProgRate, cp.CloseDate, cp.PrjColor, cp.Remark, cp.StartDate, cp.EndDate, cp.RegisterCode, cp.RegisteDate
		  FROM COLLAB_PROJECT cp
	LEFT  JOIN COLLAB_PROJECT_member cpm ON cp.PRJSEQ = cpm.PRJSEQ AND cpm.USERCODE = #{USERID}
	LEFT  JOIN COLLAB_PROJECT_MANAGER cpn ON cp.PRJSEQ = cpn.PRJSEQ AND cpn.USERCODE = #{USERID}
	LEFT  JOIN COLLAB_PROJECT cpr ON cp.PRJSEQ = cpr.PRJSEQ AND cpr.REGISTERCODE = #{USERID}
	LEFT  JOIN COLLAB_PROJECT_VIEWER cpv ON cp.PrjSeq = cpv.PrjSeq AND cpv.UserCode = #{USERID}
		 WHERE 1=1 

		 	 AND cp.CompanyCode = #{CompanyCode}


		   AND cp.ISCLOSE='N'


		   AND cp.IsClose= #{inClose}


		   AND (cpm.PRJSEQ IS NOT NULL OR  cpn.PRJSEQ IS NOT NULL OR   cpr.PRJSEQ IS NOT NULL )


		   AND (cp.PRJNAME LIKE '%' || #{seValue} || '%'
		   	OR cp.REMARK LIKE '%' || #{seValue} || '%')


			AND cp.PRJSTATUS  in 

                    #{item}


			) p

SELECT DashThema "DashThema", TaskShowCode "TaskShowCode"
  FROM collab_user_conf
 WHERE UserCode = #{USERID}
