mapper.insert:
/* collab.task.insertTaskHist */
BEGIN

		INSERT INTO collab_task_history
				(histseq, taskSeq  , modType, modItem, bfVal, afVal, RegisterCode ,RegisteDate)
		values (COLLAB_TASK_HIST_SEQ.NEXTVAL, #{taskSeq}, #{lst.modType}, #{lst.modItem}, #{lst.bfVal}, #{lst.afVal} ,#{USERID},sysdate)

END;

mapper.select:
	/* collab.hist.getTaskHistListCnt */
		SELECT count(h.histseq) 	     	
		FROM   collab_task_history h
		LEFT JOIN collab_task t ON h.TaskSeq = t.taskseq 
		LEFT JOIN sys_object_user D on D.UserCode = h.registercode

			 JOIN collab_task_map C on h.taskseq = c.taskseq
			 JOIN collab_project P on c.PrjType='P' and p.PrjSeq = c.PrjSeq
				AND P.PrjSeq in 

	                    #{item}


  WHERE	h.RegisteDate between  to_date(CONCAT(#{startDate},'000000') ,'yyyymmddhh24miss')   and to_date(CONCAT(#{endDate},'235959') ,'yyyymmddhh24miss')  	 

				AND h.registercode in 

	                    #{item}



		  	 	AND modItem  = #{modItem}

/* collab.hist.getTaskHistListCnt */
SELECT to_char(h.RegisteDate ,'yyyy-mm-dd hh24:mi') "RegisteDate", h.RegisterCode "RegisterCode", t.TaskName "TaskName" , h.ModType "ModType" , h.ModItem "ModItem" , CAST(BfVal AS VARCHAR2(2000)) "BfVal" , CAST(AfVal AS VARCHAR2(2000)) "AfVal" , Fn_BaseGetDictionary_S(#{lang}, d.MultiDisplayName) "RegisterName" , ModType, Fn_GetBaseCodeName(0,'HistoryType',modType) "ModTypeName" , ModItem, Fn_GetBaseCodeName(0,'CollabHistory',modItem) "ModItemName" ,(SELECT c.PrjType||'^'|| case when c.prjType = 'P' then p.PrjName ELSE t.DisplayName END ||'^'|| nvl(PrjColor,'')||'^'|| s.SectionName FROM collab_task_map C JOIN collab_section s on c.sectionseq = s.sectionseq LEFT JOIN collab_project P on c.PrjType='P' and p.PrjSeq = c.PrjSeq LEFT JOIN sys_object_group T on c.PrjType!='P' and t.GroupId = c.PrjSeq WHERE C.TaskSeq = h.TaskSeq and rownum=1) "PrjDesc" , (SELECT count(*) FROM collab_task_map WHERE TaskSeq = h.TaskSeq) as "PrjCount" , case modItem WHEN 'Section' then (SELECT SectionName FROM collab_section s WHERE s.SectionSeq = CAST(CAST(afval AS VARCHAR2(200)) AS NUMBER(10))) WHEN 'Status' then Fn_GetBaseCodeName(0,'CollabStatus', AfVal) ELSE CAST(AfVal AS VARCHAR2(2000)) END "AfValName" , case modItem WHEN 'Section' then (SELECT SectionName FROM collab_section s WHERE s.SectionSeq = CAST(CAST(bfval AS VARCHAR2(200)) AS NUMBER(10))) WHEN 'Status' then Fn_GetBaseCodeName(0,'CollabStatus', BfVal) ELSE CAST(BfVal AS VARCHAR2(2000)) END "BfValName" FROM collab_task_history h LEFT JOIN collab_task t ON h.TaskSeq = t.taskseq LEFT JOIN sys_object_user D on D.UserCode = h.registercode
JOIN collab_task_map C on h.taskseq = c.taskseq JOIN collab_project P on c.PrjType='P' and p.PrjSeq = c.PrjSeq AND P.PrjSeq in
#{item}
WHERE h.RegisteDate between to_date(CONCAT(#{startDate},'000000') ,'yyyymmddhh24miss') and to_date(CONCAT(#{endDate},'235959') ,'yyyymmddhh24miss')
AND h.registercode in
#{item}
AND modItem = #{modItem}
h.RegisteDate "RegisterName" t.TaskName "ModItemName" "ModTypeName" RegisteDate
ASC DESC
h.RegisteDate desc
