mapper.select:
    /* collab.report.getUserCollabReportDayListCnt */
		  SELECT NVL(COUNT(1),0) CNT
         	FROM COLLAB_REPORT_DAY crd
	  INNER JOIN COLLAB_TASK ct on ct.TaskSeq = crd.TaskSeq
		   WHERE crd.ReporterCode = #{USERID}
             AND crd.ReportDate = #{reportDate}

mapper.insert:
/* collab.report.insertUserCollabReportDay */
    INSERT INTO COLLAB_REPORT_DAY (DAYSEQ, ReporterCode, ReportDate, TaskSeq, TaskStatus, ProgRate, RegisterCode, RegistDate)
    SELECT
    	COLLAB_REPORT_DAY_SEQ.NEXTVAL
        , #{USERID}
        , #{reportDate}
        , ct.TaskSeq
        , ct.TaskStatus
        , ct.ProgRate
        , #{USERID}
        , SYSDATE
    FROM COLLAB_TASK ct
    WHERE
        ct.TaskSeq in

            #{taskSeq}

mapper.update:
/* collab.report.updateUserCollabReportDay */
    UPDATE COLLAB_REPORT_DAY
    SET TaskStatus = #{taskStatus}
        , ProgRate = #{progRate}
        , TaskTime = #{taskTime}
        , Remark = #{remark}
        , ModifierCode = #{USERID}
        , ModifyDate = SYSDATE
    WHERE      DaySeq = #{daySeq}

mapper.delete:
/* collab.report.deleteUserCollabReportDay */
    DELETE
    FROM COLLAB_REPORT_DAY
    WHERE
        DaySeq in

            #{daySeq}

mapper.select:
/* collab.report.getUserCollabReportWeekList */
SELECT crd.DaySeq as "DaySeq" , crd.ReportDate as "ReportDate" , crd.TaskSeq as "TaskSeq" , ctmap.PrjType as "PrjType" , ctmap.PrjSeq as "PrjSeq" , ct.TaskName as "TaskName" , crd.TaskStatus as "TaskStatus" , crd.ProgRate as "ProgRate" , crd.TaskTime as "TaskTime" , crd.Remark as "Remark" , s.SectionName as "SectionName" , (select count(*) from COLLAB_TASK_MAP where TaskSeq = crd.TaskSeq) as PrjCount FROM COLLAB_REPORT_DAY crd INNER JOIN COLLAB_TASK ct on ct.TaskSeq = crd.TaskSeq INNER JOIN COLLAB_TASK_MAP ctmap on ctmap.TaskSeq = ct.TaskSeq AND ctmap.PrjSeq = #{prjSeq}
AND ctmap.PrjType = 'P'
AND ctmap.PrjType != 'P'
LEFT JOIN collab_section s on ctmap.sectionSeq = s.sectionseq WHERE crd.ReporterCode = #{USERID} AND crd.ReportDate between #{startDate} and #{endDate} order by ReportDate, DaySeq

    /* collab.report.getUserCollabReportWeekListCnt */
         select NVL(COUNT(1),0) CNT
 		   FROM COLLAB_REPORT_DAY crd
     INNER JOIN COLLAB_TASK ct on ct.TaskSeq = crd.TaskSeq
	 INNER JOIN COLLAB_TASK_MAP ctmap on ctmap.TaskSeq = ct.TaskSeq AND ctmap.PrjSeq = #{prjSeq} 


            	AND ctmap.PrjType  = 'P'		


				AND ctmap.PrjType != 'P'		


      LEFT JOIN collab_section s on ctmap.sectionSeq = s.sectionseq
          WHERE crd.ReporterCode = #{USERID}
            AND crd.ReportDate between #{startDate} and #{endDate}
       order by ReportDate, DaySeq

/* collab.report.getUserCollabReportWeek */
    select
        ReportSeq as "ReportSeq"
        , WeekRemark as "WeekRemark"
        , NextPlan as "NextPlan"
    from COLLAB_REPORT
    where 1=1
        and ReporterCode = #{USERID}
        AND PrjType  = #{prjType}
        and PrjSeq = #{prjSeq}
        and StartDate = #{startDate}
        and EndDate = #{endDate}

mapper.insert:
/* collab.report.insertUserCollabReportWeek */
    INSERT INTO COLLAB_REPORT (REPORTSEQ, ReporterCode, PrjType, PrjSeq, StartDate, EndDate, WeekRemark, NextPlan, RegisterCode, RegistDate)
    VALUES (
    	COLLAB_REPORT_SEQ.NEXTVAL
        , #{USERID}
        , #{prjType}
        , #{prjSeq}
        , #{startDate}
        , #{endDate}
        , #{weekRemark}
        , #{nextPlan}
        , #{USERID}
        , SYSDATE
    )

mapper.update:
/* collab.report.updateUserCollabReportWeek */
    update COLLAB_REPORT
    set WeekRemark = #{weekRemark}
        , NextPlan = #{nextPlan}
        , ModifierCode = #{USERID}
        , ModifyDate = SYSDATE
    where
        ReportSeq = #{reportSeq}

mapper.delete:
/* collab.report.deleteUserCollabReportWeek */
    delete
    from COLLAB_REPORT
    where
        ReportSeq = #{reportSeq}
