mapper.select:
/* collab.tmpl.getTmplRequestList */
SELECT RequestSeq as "RequestSeq" , TmplKind as "TmplKind" , a.RequesterCode as "RequesterCode" , PrjSeq as "PrjSeq" , Fn_BaseGetDictionary_S(#{lang}, ur.MultiDisplayName) AS "URName" , RequestTitle as "RequestTitle" , RequestRemark as "RequestRemark" , RequestStatus as "RequestStatus" , TO_CHAR(RegisteDate, 'YYYY-MM-DD HH24:MI') AS "RegisteDate" , Fn_GetBaseCodeName(#{domainID}, 'COLLAB_KIND', a.TmplKind) as "TmplKindName" , Fn_GetBaseCodeName(#{domainID}, 'AttendState', a.RequestStatus) as "StatusName" FROM COLLAB_TMPL_REQUEST a LEFT JOIN SYS_OBJECT_USER ur ON a.RequesterCode = ur.UserCode WHERE A.CompanyCode = #{CompanyCode}
AND RequestStatus = 'ApprovalRequest'

mapper.update:

/* collab.tmpl.saveTmplRequest */
	UPDATE COLLAB_TMPL_REQUEST 
	   SET RequestStatus = #{reqStatus}
	   		, ApprovalCode = #{approvalCode}
	   		, ApprovalDate = SYSDATE 
	   		, ApprovalRemark = #{approvalRemark}
	 WHERE RequestSeq = #{requestSeq}

mapper.select:
/* collab.tmpl.getTmplList */ SELECT TmplSeq as "TmplSeq" , TmplKind as "TmplKind" , TmplName as "TmplName" , NVL(REMARK,' ') as "Remark" , TO_CHAR(RegisteDate, 'YYYY.MM.DD HH24:MI') AS "RegisteDate" , (SELECT Fn_BaseGetDictionary_S(#{lang}, c.MultiCodeName) FROM sys_base_code c WHERE c.CodeGroup ='COLLAB_KIND' AND c.Code = a.TmplKind) as "TmplKindName" , (SELECT COUNT(*) FROM sys_like b WHERE b.TargetServiceType = 'CollabTmpl' AND a.tmplSeq = b.TargetID) as "LikeCnt" , (SELECT COUNT(*) FROM sys_like b WHERE b.TargetServiceType = 'CollabTmpl' AND a.tmplSeq = b.TargetID AND b.RegisterCode = #{USERID}) as "MyLikeCnt" FROM COLLAB_TMPL_PROJECT a WHERE companyCode = #{CompanyCode}
AND TmplKind = #{tmplKind}
order by RegisteDate DESC

mapper.delete:
/* collab.tmpl.deleteTmplProject */
		DELETE FROM COLLAB_TMPL_PROJECT
		WHERE tmplSeq =  #{tmplSeq}

mapper.select:
/* collab.tmpl.getTmplSection */
	SELECT cs.SectionSeq as "SectionSeq"
		, SectionName as "SectionName"
	  FROM COLLAB_TMPL_SECTION cs
	 WHERE cs.TmplSeq = #{tmplSeq}
 -- GROUP BY cs.SectionSeq

/* collab.tmpl.getTmplTaskList */
SELECT b.TaskSeq as "TaskSeq" , B.TaskName as "TaskName" , B.Remark as "Remark" , B.Label as "Label" , B.ParentKey as "ParentKey" , '' as "StartDate" , '' as "EndDate" , B.TaskStatus as "TaskStatus" , B.CloseDate as "CloseDate" , B.ModifierCode as "ModifierCode" , B.ModifyDate as "ModifyDate" , B.SectionSeq as "SectionSeq" , 0 as "Comment" , 0 as "ProgRate" ,'P' as "PrjType" ,#{authSave} as "authSave" FROM COLLAB_TMPL_TASK B WHERE b.TmplSeq = #{tmplSeq} AND B.ParentKey = 0
AND SectionSeq = #{sectionSeq}
ORDER BY B.workOrder ASC , B.EndDate

/* collab.tmpl.getTmplTaskListCnt */
	SELECT COUNT(0)
	FROM COLLAB_TMPL_TASK B
   WHERE b.TmplSeq = #{tmplSeq} 
	 AND B.ParentKey = 0

		AND SectionSeq = #{sectionSeq}

mapper.update:
/* collab.tmpl.changeTmplTaskSection */
	UPDATE COLLAB_TMPL_TASK 
	   SET sectionSeq = #{sectionSeq}
	 WHERE tmplSeq =  #{tmplSeq}
	   AND taskSeq =  #{taskSeq}

/* collab.tmpl.changeTmplTaskOrder */
BEGIN

		UPDATE COLLAB_TMPL_TASK 
		   SET Workorder= #{lst.workOrder}
		 WHERE tmplSeq =  #{lst.taskSeq}

END;

mapper.insert:
	/* collab.tmpl.saveTmpl */
		UPDATE COLLAB_TMPL
		   SET tmplName =  #{tmplName}
   		       ,StartDate=  #{startDate}
               ,PrjColor=  #{prjColor}
               ,Remark=  #{remark}
               ,ModifierCode=  #{USERID}
               ,ModifyDate=  SYSDATE
		 WHERE prjSeq =  #{prjSeq}

mapper.delete:
/* collab.tmpl.deleteTmplTask */
	DELETE FROM COLLAB_TMPL_TASK  
	 WHERE 

	 	tmplSeq =  #{tmplSeq}


	 	taskSeq =  #{taskSeq}

mapper.select:
/* collab.tmpl.getExistsTmplSection */
	SELECT COUNT(*)
	  FROM COLLAB_TMPL_SECTION
	 WHERE tmplSeq  = #{tmplSeq}
	   AND SectionName = #{sectionName}

mapper.insert:
/* collab.tmpl.saveTmplSection */
	UPDATE COLLAB_TMPL_SECTION
	   SET sectionName =#{sectionName}
	   		,ModifierCode= #{USERID}
	   		,ModifyDate = SYSDATE
	 WHERE 	sectionSeq = 	#{sectionSeq}

mapper.delete:
/* collab.tmpl.deleteTmplSection */
	DELETE FROM COLLAB_TMPL_SECTION 
	 WHERE 

	 	tmplSeq =  #{tmplSeq}


	 	sectionSeq =  #{sectionSeq}

mapper.insert:
	/* collab.tmpl.saveTmplTask */
		UPDATE COLLAB_TMPL_TASK
		   SET taskName =  #{taskName}
               ,Remark=  #{remark}
               ,Label=  #{label}
               ,ModifierCode=  #{USERID}
               ,ModifyDate=  SYSDATE
		 WHERE taskSeq =  #{taskSeq}
