mapper.update:
UPDATE sys_object_user_info
SET PortalOption = #{portalOption}
WHERE UserCode = #{userCode}

mapper.select:
SELECT NVL(PortalOption, '') AS "PortalOption"
FROM sys_object_user_info
WHERE UserCode = #{userCode}

SELECT ApprovalList.ProcessID AS "ProcessID" , ApprovalList.WorkItemID AS "WorkItemID" , ApprovalList.PerformerID AS "PerformerID" , ApprovalList.FormInstID AS "FormInstID" , ApprovalList.ProcessDescriptionID AS "ProcessDescriptionID" , ApprovalList.FormSubject AS "FormSubject" , ApprovalList.FormPrefix AS "FormPrefix" , ApprovalList.FormID AS "FormID" , ApprovalList.InitiatorID AS "InitiatorID" , ApprovalList.InitiatorName AS "InitiatorName" , ApprovalList.UserCode AS "UserCode" , ApprovalList.UserName AS "UserName" , ApprovalList.FormSubKind AS "FormSubKind" , ApprovalList.Created AS "Created" , ApprovalList.TaskID AS "TaskID" , ApprovalList.PhotoPath AS "PhotoPath" , ApprovalList.DomainDataContext AS "DomainDataContext" , ApprovalList.SchemaContext AS "SchemaContext" , ApprovalList.BusinessData1 AS "BusinessData1" , ApprovalList.BusinessData2 AS "BusinessData2" , ApprovalList.IsFile AS "IsFile" , ApprovalList.IsComment AS "IsComment" , ApprovalList.ProcessName AS "ProcessName" , ApprovalList.Priority AS "Priority" FROM ( SELECT A.ProcessID , B.WorkItemID , C.PerformerID , A.FormInstID , A.ProcessDescriptionID , A.DocSubject AS FormSubject , D.FormPrefix , D.FormID , A.InitiatorID , Fn_BaseGetDictionary_S(#{lang}, A.InitiatorName) AS InitiatorName , C.UserCode , C.UserName , C.SubKind AS FormSubKind , TO_CHAR(B.Created, 'YYYY.MM.DD HH24:MI') AS Created , B.TaskID , U.PhotoPath , E.DomainDataContext , S.SchemaContext , D.BusinessData1 , D.BusinessData2 , D.IsFile , D.IsComment , A.ProcessName , D.Priority FROM JWF_Process A INNER JOIN JWF_Workitem B ON A.ProcessID = B.ProcessID INNER JOIN JWF_Performer C ON B.WorkItemID = C.WorkItemID INNER JOIN JWF_ProcessDescription D ON A.ProcessDescriptionID = D.ProcessDescriptionID LEFT OUTER JOIN jwf_domaindata E ON A.ProcessID = E.ProcessID LEFT OUTER JOIN sys_object_user U ON U.UserCode = A.InitiatorID LEFT OUTER JOIN jwf_forms F ON D.FormID = F.FormID LEFT OUTER JOIN jwf_formsschema S ON F.SchemaID = S.SchemaID WHERE C.UserCode = #{userID} AND C.SubKind IN ('T000','T004','T005','T006','T008','T009','T011','T016','T017','T018','T019') AND C.State = 1 AND B.State = 288 AND A.ProcessState = 288 AND A.DeleteDate IS NULL
AND (D.BusinessData1 IS NULL
OR D.BusinessData1 IN
#{item}
)
AND D.BusinessData1 = #{businessData1}
UNION ALL
SELECT A.ProcessID , B.WorkItemID , C.PerformerID , A.FormInstID , A.ProcessDescriptionID , A.DocSubject AS FormSubject , D.FormPrefix , D.FormID , A.InitiatorID , Fn_BaseGetDictionary_S(#{lang}, A.InitiatorName) AS InitiatorName , C.UserCode , C.UserName , C.SubKind AS FormSubKind , TO_CHAR(B.Created, 'YYYY.MM.DD HH24.MI') AS Created , B.TaskID , U.PhotoPath , E.DomainDataContext , S.SchemaContext , D.BusinessData1 , D.BusinessData2 , D.IsFile , D.IsComment , A.ProcessName , D.Priority FROM JWF_Process A INNER JOIN JWF_Workitem B ON A.ProcessID = B.ProcessID INNER JOIN JWF_Performer C ON B.WorkItemID = C.WorkItemID INNER JOIN JWF_ProcessDescription D ON A.ProcessDescriptionID = D.ProcessDescriptionID LEFT OUTER JOIN jwf_domaindata E ON A.ProcessID = E.ProcessID LEFT OUTER JOIN sys_object_user U ON U.UserCode = A.InitiatorID LEFT OUTER JOIN jwf_forms F ON D.FormID = F.FormID LEFT OUTER JOIN jwf_formsschema S ON F.SchemaID = S.SchemaID WHERE B.DeputyID = #{userID} AND C.SubKind IN ('T000','T004','T005','T006','T008','T009','T011','T016','T017') AND C.State = 1 AND B.State = 288 AND A.ProcessState = 288 AND A.DeleteDate IS NULL
AND (D.BusinessData1 IS NULL
OR D.BusinessData1 IN
#{item}
)
AND D.BusinessData1 = #{businessData1}
) ApprovalList WHERE ROWNUM <= 10 ORDER BY Priority DESC, Created DESC

