#{item}
)B

/* queryID : user.board.selectTreeFolderMenu */ SELECT SOF.FolderID AS "FolderID" , SOF.MemberOf AS "MemberOf" , SOF.FolderType AS "itemType" , SOF.FolderType AS "FolderType" , SOF.FolderPath AS "FolderPath" , SOF.MenuID AS "MenuID" , (CONCAT('board_', SOF.FolderType)) AS "type" , NVL(FN_BASEGETDICTIONARY_S(#{lang}, SOF.MultiDisplayName), SOF.DisplayName) AS "FolderName" , SOF.MemberOf AS "MemeberOf" , SOF.DisplayName AS "DisplayName" , SOF.SortPath AS "SortPath" , (SELECT COUNT(0) FROM SYS_OBJECT_FOLDER self WHERE self.MemberOf=sof.FolderID AND self.IsUse = 'Y' AND self.DeleteDate IS NULL) AS "hasChild" , (DECODE(INSTR(SOF.OwnerCode, #{userCode}), 0, 'N', 'Y')) AS "OwnerFlag" , NVL(SOF.IsMobileSupport, 'N') AS "IsMobileSupport" , NVL(SOF.Reserved4, 'N') AS "IsContents" FROM SYS_OBJECT_FOLDER SOF LEFT JOIN SYS_OBJECT_DOMAIN SOD ON SOF.DomainID = SOD.DomainID
LEFT JOIN BOARD_CONFIG BC ON BC.FolderID = SOF.FolderID
WHERE 1=1
AND SOF.MenuID = #{menuID}
AND SOF.MenuID = #{menuID}
AND (SOF.MenuID = #{menuID} OR SOF.DomainID = 0)
AND SOF.ObjectType = 'Community'
AND SOF.ObjectType = #{bizSection}
AND (SOF.FolderType = 'Root' OR REGEXP_LIKE (SOF.FolderPath, (SELECT LISTAGG(FolderPath||FolderID || ';', '|') WITHIN GROUP(ORDER BY FolderID) FROM SYS_OBJECT_FOLDER WHERE FolderType = 'Root')))
AND (SOF.FolderType = 'DocRoot' OR REGEXP_LIKE (SOF.FolderPath, (SELECT LISTAGG(FolderPath||FolderID || ';', '|') WITHIN GROUP(ORDER BY FolderID) FROM SYS_OBJECT_FOLDER WHERE FolderType = 'DocRoot')))
AND (SOF.foldertype IN ('Root','Folder') OR SOF.reserved4='Y' ) AND SOF.DomainID = #{domainID}
AND SOF.DeleteDate IS NULL AND SOF.IsUse = 'Y' AND SOF.IsDisplay = 'Y'
AND SOF.FolderID IN
#{item}
AND SOF.FolderType NOT IN ('QuickComment', 'Contents')
AND SOF.IsMobileSupport = 'Y'
AND (SOF.FolderType IN ('Root', 'Folder') OR BC.UseOwnerProcess = 'N')
ORDER BY SOF.MenuID, SOF.FolderPath NULLS FIRST, SOF.SortKey ASC

SELECT SOF.MenuID AS "groupValue" ,( SELECT FN_BASEGETDICTIONARY_S(#{lang}, MultiDisplayName) FROM SYS_OBJECT_MENU WHERE 1=1 AND MenuID = SOF.MenuID) AS "groupText" , SOF.MenuID||'_'||SOF.FolderID AS "optionValue" , NVL(FN_BASEGETDICTIONARY_S(#{lang}, SOF.MultiDisplayName), SOF.DisplayName ) AS "optionText" FROM SYS_OBJECT_FOLDER SOF INNER JOIN BOARD_CONFIG BC ON ( BC.FolderID = SOF.FolderID ) WHERE 1=1 AND BC.UseOwnerProcess = 'N' AND SOF.FolderType NOT IN ('Root', 'Folder', 'QuickComment', 'Contents') AND SOF.IsUse = 'Y' AND SOF.IsDisplay = 'Y' AND SOF.ObjectType = 'Board' AND SOF.DeleteDate IS NULL AND ( SOF.MenuID IN
#{item}
OR SOF.DomainID = 0 ) ORDER BY MenuID, FolderPath, SortKey ASC

SELECT * FROM( SELECT BM.MessageID AS "MessageID", BM.FolderID AS "FolderID", BM.Version AS "Version", BM.Subject AS "Subject", TO_CHAR(BM.RegistDate,'YYYY-MM-DD HH24:MI:SS') AS "RegistDate", SOF.MenuID AS "MenuID" FROM BOARD_MESSAGE BM INNER JOIN SYS_OBJECT_FOLDER SOF ON BM.FolderID = SOF.FolderID WHERE BM.DeleteDate IS NULL AND SOF.DeleteDate IS NULL AND SOF.IsUse = 'Y' AND BM.MsgType NOT IN ('N', 'B') AND BM.MsgState IN ('C', 'A') AND BM.UseAnonym = 'N' AND (BM.ReservationDate IS NULL OR BM.ReservationDate < TO_DATE(#{localCurrentDate},'YYYY-MM-DD HH24:MI:SS')) AND (NVL(BM.UseSecurity,'N') != 'Y' OR (NVL(BM.UseSecurity,'N') = 'Y' AND BM.CreatorCode = #{sessionUserCode})) AND (BM.ExpiredDate IS NULL OR BM.ExpiredDate >= TO_DATE(#{localCurrentDate},'YYYY-MM-DD HH24:MI:SS')) AND CreatorCode = #{userCode} AND BM.FolderID IN
#{item}
ORDER BY BM.RegistDate DESC ) a WHERE ROWNUM <= 5

SELECT OwnerCode AS "managerCode",
NVL((SELECT ManagerCode FROM board_category WHERE FolderID = #{folderID} AND CategoryID = #{categoryID}), '') AS "categoryManagerCode",
NVL(isAdminNotice, 'N') AS "isAdminNotice" FROM sys_object_folder WHERE 1=1 AND FolderID = #{folderID}
