mapper.select:
SELECT COUNT(*) FROM ( SELECT c.CU_ID, ca.Status FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.RequestID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ( c.GUBUN = 'C' OR c.GUBUN IS NULL ) AND ca.ReceiveID = #{CU_ID} AND c.DN_ID = #{DN_ID}
AND ca.Status = #{Status}
UNION ALL
SELECT c.CU_ID, ca.Status FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.ReceiveID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ( c.GUBUN = 'C' OR c.GUBUN IS NULL ) AND ca.RequestID = #{CU_ID} AND c.DN_ID = #{DN_ID}
AND ca.Status = #{Status}
UNION ALL
SELECT * FROM ( SELECT c.CU_ID, '' AS Status FROM COMMUNITY c WHERE c.CU_ID NOT IN ( SELECT CU_ID FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.RequestID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ca.ReceiveID = #{CU_ID}
UNION ALL
SELECT CU_ID FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.ReceiveID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ca.RequestID = #{CU_ID} ) AND c.CU_ID != #{CU_ID} AND c.AppStatus IN('RV','RS','UA','UD') AND ( c.GUBUN = 'C' OR c.GUBUN IS NULL ) AND c.DN_ID = #{DN_ID} ) A WHERE 1=1 AND Status = #{Status} ) B

SELECT B.CU_ID AS "CU_ID" , B.PartiID AS "PartiID" , B.CommunityName AS "CommunityName" , B.DisplayName AS "DisplayName" , B.MailAddress AS "MailAddress" , B.OperatorCode AS "OperatorCode" , B.RegProcessDate AS "RegProcessDate" , B.MemberCount AS "MemberCount" , B.RequestDate AS "RequestDate" , B.ProcessingDate AS "ProcessingDate" , B.RecipientID AS "RecipientID" , B.RecipientName AS "RecipientName" , B.RecipientMailAddress AS "RecipientMailAddress" , B.RequesterID AS "RequesterID" , B.RequesterName AS "RequesterName" , B.RequesterMailAddress AS "RequesterMailAddress" , B.statusNm AS "statusNm" , B.Status AS "Status" , B.keyNum AS "keyNum" , B.SearchTitle AS "SearchTitle" FROM ( SELECT c.CU_ID , TO_CHAR(ca.PartiID) AS PartiID , NVL(FN_GETSELDICTIONARY(#{lang},CONCAT('CU_',c.CU_ID), c.DN_ID),c.CommunityName)AS CommunityName , (SELECT DisplayName FROM SYS_OBJECT_USER WHERE UserCode = c.OperatorCode)AS DisplayName , (SELECT MailAddress FROM SYS_OBJECT_USER WHERE UserCode = c.OperatorCode)AS MailAddress , c.OperatorCode , c.RegProcessDate AS RegProcessDate , NVL(D.MemberCNT, 0) AS MemberCount , ca.RequestDate AS RequestDate , ca.ProcessingDate AS ProcessingDate , RecipientID , NVL( (SELECT DisplayName FROM SYS_OBJECT_USER WHERE UserCode = ca.RecipientID),'')AS RecipientName , (SELECT MailAddress FROM SYS_OBJECT_USER WHERE UserCode = ca.RecipientID)AS RecipientMailAddress , RequesterID , NVL( (SELECT DisplayName FROM SYS_OBJECT_USER WHERE UserCode = ca.RequesterID),'')AS RequesterName , (SELECT MailAddress FROM SYS_OBJECT_USER WHERE UserCode = ca.RequesterID)AS RequesterMailAddress , FN_BASEGETDICTIONARY_S(#{lang}, Fn_GetBaseMultiCodeName(c.DN_ID, 'CuPartiStatus', DECODE(ca.Status, 'CuPartiStatus', '', ca.Status))) AS statusNm , ca.Status , '1' AS keyNum , NVL(c.SearchTitle,'')AS SearchTitle FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.RequestID = c.CU_ID LEFT JOIN (SELECT CU_ID , COUNT(*) AS MemberCNT FROM community_member WHERE MemberStatus ='R' AND MemberLevel !='0' AND DetailStatus = 'RV' GROUP BY CU_ID ) D ON D.CU_ID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ( c.GUBUN = 'C' OR c.GUBUN IS NULL ) AND ca.ReceiveID = #{CU_ID} AND c.DN_ID = #{DN_ID}
AND ca.Status = #{Status}
UNION ALL
SELECT c.CU_ID , TO_CHAR(ca.PartiID) AS PartiID , NVL(FN_GETSELDICTIONARY(#{lang},CONCAT('CU_',c.CU_ID), c.DN_ID),c.CommunityName)AS CommunityName , (SELECT DisplayName FROM SYS_OBJECT_USER WHERE UserCode = c.OperatorCode)AS DisplayName , (SELECT MailAddress FROM SYS_OBJECT_USER WHERE UserCode = c.OperatorCode)AS MailAddress , c.OperatorCode , c.RegProcessDate AS RegProcessDate , c.MemberCount , ca.RequestDate AS RequestDate , ca.ProcessingDate AS ProcessingDate , RecipientID , NVL( (SELECT DisplayName FROM SYS_OBJECT_USER WHERE UserCode = RecipientID),'')AS RecipientName , (SELECT MailAddress FROM SYS_OBJECT_USER WHERE UserCode = RecipientID)AS RecipientMailAddress , RequesterID , NVL( (SELECT DisplayName FROM SYS_OBJECT_USER WHERE UserCode = RequesterID),'')AS RequesterName , (SELECT MailAddress FROM SYS_OBJECT_USER WHERE UserCode = RequesterID)AS RequesterMailAddress , FN_BASEGETDICTIONARY_S(#{lang}, Fn_GetBaseMultiCodeName(c.DN_ID, 'CuPartiStatus', DECODE(ca.Status, 'CuPartiStatus', '', ca.Status))) AS statusNm , ca.Status , '2' AS keyNum , NVL(c.SearchTitle,'')AS SearchTitle FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.ReceiveID = c.CU_ID LEFT JOIN (SELECT CU_ID , COUNT(*) AS MemberCNT FROM community_member WHERE MemberStatus ='R' AND MemberLevel !='0' AND DetailStatus = 'RV' GROUP BY CU_ID ) D ON D.CU_ID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ( c.GUBUN = 'C' OR c.GUBUN IS NULL ) AND ca.RequestID = #{CU_ID} AND c.DN_ID = #{DN_ID}
AND ca.Status = #{Status}
UNION ALL
SELECT * FROM ( SELECT c.CU_ID , '' AS PartiID , NVL(FN_GETSELDICTIONARY(#{lang},CONCAT('CU_',c.CU_ID), c.DN_ID),c.CommunityName)AS CommunityName , (SELECT DisplayName FROM SYS_OBJECT_USER WHERE UserCode = c.OperatorCode)AS DisplayName , (SELECT MailAddress FROM SYS_OBJECT_USER WHERE UserCode = c.OperatorCode)AS MailAddress , c.OperatorCode , NVL(c.RegProcessDate,'')AS RegProcessDate , c.MemberCount , NULL AS RequestDate , NULL AS ProcessingDate , '' AS RecipientID , '' AS RecipientName , '' AS RecipientMailAddress , '' AS RequesterID , '' AS RequesterName , '' AS RequesterMailAddress , '' AS statusNm , '' AS Status , '3' AS keyNum , NVL(c.SearchTitle,'')AS SearchTitle FROM COMMUNITY c LEFT JOIN (SELECT CU_ID , COUNT(*) AS MemberCNT FROM community_member WHERE MemberStatus ='R' AND MemberLevel !='0' AND DetailStatus = 'RV' GROUP BY CU_ID ) D ON D.CU_ID = c.CU_ID WHERE c.CU_ID NOT IN ( SELECT CU_ID FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.RequestID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ca.ReceiveID = #{CU_ID}
UNION ALL
SELECT CU_ID FROM COMMUNITY c INNER JOIN COMMUNITY_AFFILIATES ca ON ca.ReceiveID = c.CU_ID WHERE 1=1 AND c.AppStatus IN('RV','RS','UA','UD') AND ca.RequestID = #{CU_ID} ) AND c.CU_ID != #{CU_ID} AND c.AppStatus IN('RV','RS','UA','UD') AND ( c.GUBUN = 'C' OR c.GUBUN IS NULL ) AND c.DN_ID = #{DN_ID} ) A WHERE 1=1 AND A.Status = #{Status} ) B
CommunityName DisplayName RegProcessDate TO_NUMBER(MemberCount) RequesterName RequestDate RecipientName ProcessingDate Status statusNm
ASC DESC

