mapper.select:
/* groupware.vacation.selectVacationDayList */ SELECT UR_Code AS "UR_Code" , YEAR AS "YEAR" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , DisplayName AS "DisplayName" , EnterDate AS "EnterDate" , RetireDate AS "RetireDate" , CompanyCode AS "CompanyCode" , ExpDateStart AS "ExpDateStart" , ExpDateEnd AS "ExpDateEnd" , UseStartDate AS "UseStartDate" , UseEndDate AS "UseEndDate" , RegisterCode AS "RegisterCode" , Reason AS "Reason" , CAST(VacDay AS FLOAT) AS "VacDay" , CAST(UseVacDay AS FLOAT) AS "UseVacDay" , CAST(VacDay - UseVacDay AS FLOAT) AS "RemainVacDay" , CAST(LastVacDay AS FLOAT) AS "LastVacDay" , CAST(LongVacDay AS FLOAT) AS "LongVacDay" FROM ( SELECT A.UR_Code , A.YEAR , CAST(TO_CHAR(A.VacDay, 'fm90.000') AS FLOAT) AS VacDay , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , Fn_BaseGetDictionary_S(#{lang}, U.MultiDisplayName) AS DisplayName , U.EnterDate , U.RetireDate , B.CompanyCode , TO_CHAR(TO_DATE(A.UseStartDate, 'YYYY-MM-DD'), 'YY.MM.DD') AS ExpDateStart , TO_CHAR(TO_DATE(A.UseEndDate, 'YYYY-MM-DD'), 'YY.MM.DD') AS ExpDateEnd , A.UseStartDate , A.UseEndDate , A.RegisterCode ,(SELECT NVL(SUM(u.VacDay), 0) FROM vm_vacationinfo_day u LEFT JOIN SYS_BASE_CODE k ON u.VacFlag = k.CODE AND k.CodeGroup = 'VACATION_TYPE' AND k.IsUse ='Y' AND k.DomainID = #{domainID} AND k.Reserved1 ='+' LEFT JOIN SYS_BASE_CODE t ON k.ReservedInt = t.CodeID WHERE A.ur_code = u.UR_Code AND u.VacDate BETWEEN to_char(to_date(A.UseStartDate, 'yyyy-mm-dd'), 'yyyy-mm-dd') AND to_char(to_date(A.UseEndDate , 'yyyy-mm-dd'), 'yyyy-mm-dd') AND A.VacKind = t.Code) AS UseVacDay , A.VmComment AS Reason , A.LastVacDay , A.LongVacDay , row_number() over(ORDER BY
B.MultiDeptName U.MultiDisplayName U.EnterDate B.MultiJobPositionName A.UseStartDate A.VacDay JobPositionSortKey
ASC DESC
JobPositionSortKey
) RNUM FROM vm_vacationplan A INNER JOIN sys_object_user U ON EnterDate is not null AND A.UR_Code = U.UserCode INNER JOIN sys_object_user_basegroup B ON U.UserCode = b.UserCode AND JobType = 'Origin' WHERE YEAR = #{year}
AND B.CompanyCode = #{domainCode}
and A.VacKind='PUBLIC'
AND B.DeptName LIKE '%' || #{schTxt} || '%'
AND DisplayName LIKE '%' || #{schTxt} || '%'
AND U.RetireDate IS NULL AND B.DeptCode NOT LIKE '%RetireDept'
AND U.RetireDate IS NOT NULL
AND A.UR_CODE = #{urCode}
B.JobTitleSortKey ASC
B.JobLevelSortKey ASC
B.JobPositionSortKey ASC
DisplayName ASC
, B.SortKey ASC
) A
order by rnum asc

