/* groupware.vacation.selectVacationDayListV2 */
SELECT UR_Code AS "UR_Code" , YEAR AS "YEAR" , VacDay AS "VacDay" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , DisplayName AS "DisplayName" , EnterDate AS "EnterDate" , RetireDate AS "RetireDate" , CompanyCode AS "CompanyCode" , ExpDateStart AS "ExpDateStart" , ExpDateEnd AS "ExpDateEnd" , UseStartDate AS "UseStartDate" , UseEndDate AS "UseEndDate" , RegisterCode AS "RegisterCode" , UseVacDay AS "UseVacDay" , (VacDay - UseVacDay) AS "RemainVacDay" , Reason AS "Reason" FROM( SELECT A.UR_Code , A.YEAR , cast(cast(TO_CHAR(A.VacDay, 'fm90.000') as DECIMAL(9,3)) as float) AS VacDay , B.DeptName , B.DeptCode , B.JobPositionName , B.JobPositionSortKey , B.SortKey , B.DisplayName , B.EnterDate , B.RetireDate , B.CompanyCode , TO_CHAR(TO_DATE(A.UseStartDate,'YYYYMMDD'),'YYYY.MM.DD') AS ExpDateStart , TO_CHAR(TO_DATE(A.UseEndDate,'YYYYMMDD'),'YYYY.MM.DD') AS ExpDateEnd , A.UseStartDate , A.UseEndDate , A.RegisterCode , (SELECT NVL(SUM(VacDay), 0) FROM VM_VACATIONINFO WHERE TO_DATE(Sdate,'YYYY-MM-DD') between TO_DATE(A.UseStartDate,'YYYYMMDD') and TO_DATE(A.UseEndDate,'YYYYMMDD') AND TO_DATE(Edate,'YYYY-MM-DD') between TO_DATE(A.UseStartDate,'YYYYMMDD') and TO_DATE(A.UseEndDate,'YYYYMMDD') AND UR_Code = A.UR_Code AND GUBUN in ('VACATION_APPLY','VACATION_PUBLIC','VACATION_CANCEL','VACATION_PUBLIC_CANCEL') AND VacFlag IN (SELECT A.Code FROM ( SELECT * FROM SYS_BASE_CODE WHERE CodeGroup = 'VACATION_TYPE' AND IsUse = 'Y' AND Reserved1 = '+' AND DomainID = #{domainID}
) A, ( SELECT * FROM SYS_BASE_CODE WHERE CodeGroup = 'VACATION_KIND' AND IsUse = 'Y' AND DomainID = 0 AND Code = 'PUBLIC' ) B WHERE A.ReservedInt = B.CodeID ) ) AS UseVacDay , A.VmComment AS Reason FROM vm_vacationplan A INNER JOIN ( SELECT A.UserCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , A.EnterDate , A.RetireDate , A.SortKey , B.CompanyCode , B.DeptCode , Fn_BaseGetDictionary_S(#{lang},NVL(B.DeptName, B.MultiDeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , B.JobPositionSortKey , A.TargetYear FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR)) END AS TargetYear from sys_object_user where EnterDate is not null ) A INNER JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin'
WHERE B.CompanyCode = #{domainCode}
) B ON A.UR_Code = B.UserCode and A.year = B.TargetYear and A.VacKind='PUBLIC' ) TBL WHERE 1=1
AND DeptName LIKE '%' || #{schTxt} || '%'
AND DisplayName LIKE '%' || #{schTxt} || '%'
AND RetireDate IS NULL AND DeptCode NOT LIKE '%RetireDept'
AND (RetireDate != '' OR RetireDate IS NOT NULL) AND EXTRACT(YEAR FROM TO_DATE(RetireDate)) = #{year}
AND UR_CODE = #{urCode}
DeptName DisplayName EnterDate JobPositionName VacDay
ASC DESC
JobPositionSortKey, SortKey ASC

/* groupware.vacation.selectVacationDayListCntV2 */ SELECT COUNT(*) AS CNT FROM vm_vacationplan A INNER JOIN ( SELECT A.UserCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , A.EnterDate , A.RetireDate , A.SortKey , B.CompanyCode , B.DeptCode , Fn_BaseGetDictionary_S(#{lang},NVL(B.DeptName, B.MultiDeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , B.JobPositionSortKey , A.TargetYear FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR)) END AS TargetYear from sys_object_user where EnterDate is not null) A INNER JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin'
WHERE B.CompanyCode = #{domainCode}
) B ON A.UR_Code = B.UserCode and A.year = B.TargetYear and A.VacKind='PUBLIC' WHERE 1=1
AND B.DeptName LIKE '%' || #{schTxt} || '%'
AND DisplayName LIKE '%' || #{schTxt} || '%'
AND B.RetireDate IS NULL AND B.DeptCode NOT LIKE '%RetireDept'
AND (B.RetireDate != '' OR B.RetireDate IS NOT NULL) AND EXTRACT(YEAR FROM TO_DATE(B.RetireDate)) = #{year}
AND A.UR_CODE = #{urCode}

