mapper.update:
/* groupware.vacation.updateVacDay */

		SELECT fn_GetLastVacDay(#{urCode}, #{year}, #{domainCode}) AS "LastVacDay" FROM dual

	UPDATE vm_vacationplan
		SET LongVacDay = #{vacDay} ,
	        LastVacDay = #{LastVacDay},
	        VacDay = #{vacDay} + #{LastVacDay} 
	WHERE UR_Code = #{urCode}
		AND YEAR = #{year}

		AND VacKind = #{vacKind}

/* groupware.vacation.updateLastVacDay */
SELECT TargetYear AS "TargetYear", UseStartDate AS "UseStartDate", UseEndDate AS "UseEndDate" ,fn_GetLastVacDay(#{urCode}, TargetYear ,DomainCode) AS "LastVacDay" ,(select VacDay from vm_vacationplan where ur_code = #{urCode} and year = TargetYear and VacKind='PUBLIC') as "BeforeVacDay" ,(select LongVacDay + fn_GetLastVacDay(#{urCode}, TargetYear , DomainCode) from vm_vacationplan where ur_code = #{urCode} and year = TargetYear and VacKind='PUBLIC') as "AfterVacDay" FROM (SELECT EXTRACT(YEAR FROM TO_DATE(CONCAT(year,'0101'),'YYYYMMDD') + (INTERVAL '1' YEAR)) AS TargetYear ,TO_CHAR(TO_DATE(UseStartDate,'YYYYMMDD')+ (INTERVAL '1' YEAR ),'YYYYMMDD') AS UseStartDate ,TO_CHAR(TO_DATE(UseEndDate,'YYYYMMDD')+ (INTERVAL '1' YEAR ),'YYYYMMDD') AS UseEndDate , (SELECT CompanyCode FROM sys_object_user_basegroup WHERE UserCode = #{urCode} AND JobType = 'Origin') AS DomainCode FROM vm_vacationplan WHERE ur_code= #{urCode} AND #{sDate} BETWEEN UseStartDate AND UseEndDate AND VacKind='PUBLIC')
UPDATE vm_vacationplan SET LastVacDay = #{LastVacDay} , VacDay = LongVacDay + #{LastVacDay} WHERE UR_Code = #{urCode} AND YEAR = #{TargetYear} AND VacKind = 'PUBLIC'

/* groupware.vacation.updateVacPeriodCode */ { CALL DECLARE BEGIN
UPDATE sys_base_code SET Reserved1 = #{item.StartDate}, Reserved2 = #{item.EndDate}, ModifierCode = #{modifierCode}, ModifyDate = SYSDATE WHERE CodeGroup = 'VacationPromotionPeriod' AND Code = #{item.Code} AND DomainID = #{domainID};
END }

