/* groupware.vacation.selectMyVacationInfoList */
SELECT VacationInfoID "VacationInfoID" , APPDATE "APPDATE" , ENDDATE "ENDDATE" , GUBUN "GUBUN" , Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) AS "GubunName" , VacYear "VacYear" , Sdate "Sdate" , Edate "Edate" , VacFlag "VacFlag" , VacOffFlag "VacOffFlag" , cast(cast(TO_CHAR(e.VacDay, 'fm90.000') as DECIMAL(9,3)) as float) AS "VacDay" , Reason "Reason" , WorkItemID "WorkItemID" , e.ProcessID "ProcessID" , nvl(AR.ProcessID,0) AS "EXIST_APPROVAL_FORM" , nvl(AR2.ReqSeq,0) AS "EXIST_REQUEST_FORM" , nvl(AR2.ReqTitle,'') AS "ReqTitle" , (CASE WHEN (GUBUN = 'VACATION_CANCEL' OR GUBUN = 'VACATION_PUBLIC' OR GUBUN = 'VACATION_PUBLIC_CANCEL' ) THEN '('|| Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName)|| ')'|| Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) ELSE Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) END ) AS "VACTEXT" FROM vm_vacationplan A
LEFT JOIN SYS_BASE_CODE k ON k.CodeGroup = 'VACATION_TYPE' AND k.IsUse ='Y' AND k.DomainID = #{domainID}
JOIN vm_vacationinfo e ON a.UR_Code = e.UR_Code AND e.Sdate BETWEEN to_char(to_date(a.UseStartDate,'yyyy-mm-dd'),'yyyy-mm-dd') AND to_char(to_date(a.UseEndDate ,'yyyy-mm-dd'),'yyyy-mm-dd')
AND k.Code = e.VacFlag
AND a.VacKind = e.VacFlag
LEFT JOIN attend_request AR ON e.ProcessID = AR.ProcessID LEFT JOIN attend_request AR2 ON e.ProcessID = AR2.ReqSeq LEFT JOIN sys_base_code C ON e.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID =0 LEFT JOIN sys_base_code D ON e.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} WHERE A.UR_Code = #{urCode} AND a.VacKind = #{VacKind} AND a.UseStartDate = #{UseStartDate}
VacationInfoID DESC
DisplayName APPDATE ENDDATE VacFlagName VacYear Sdate Edate VacDay Reason GubunName VACTEXT DeptName VacationInfoID
ASC DESC

	/* groupware.vacation.selectMyVacationInfoListCnt */
