),'YYYY-MM-DD') END AS VacYearRange , CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR )) END AS TargetYear from sys_object_user where ENTERDATE is not null) A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' LEFT JOIN vm_vacationplan E ON A.UserCode = E.UR_Code and E.year = TargetYear and E.VacKind='PUBLIC' ) B ON A.UR_Code = B.UserCode AND TO_DATE(A.Sdate,'YYYY-MM-DD') >= B.VacYearRange AND TO_DATE(A.Edate,'YYYY-MM-DD') <= TO_DATE(B.VacYearRange) + (INTERVAL '1' YEAR) - (INTERVAL '1' DAY) LEFT JOIN sys_base_code C ON A.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID =0 LEFT JOIN sys_base_code D ON A.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} LEFT JOIN attend_request AR ON A.ProcessID = AR.ProcessID LEFT JOIN attend_request AR2 ON A.ProcessID = AR2.ReqSeq WHERE VacYear = #{year}
