, (SELECT VACPLAN FROM vm_useage_plan WHERE UR_CODE = #{urCode} AND YEAR = SUBSTR(EnterDate,1,4)) AS "VACPLAN"
, (SELECT VACPLAN FROM vm_useage_plan WHERE UR_CODE = #{urCode} AND YEAR = Tyear) AS "VACPLAN"
