ionDateRage('F','Code2' ,VacLimit ,#{domainCode}) END AS VacLimitOneFrom , CASE WHEN EXTRACT(YEAR FROM TO_DATE(fn_attend_getPromotionDateRage('F','Code3' ,VacLimit ,#{domainCode}),'YYYY-MM-DD')) < #{year} THEN fn_attend_getPromotionDateRage('F','Code3' ,ADD_MONTHS(VacLimit, 12) ,#{domainCode}) ELSE fn_attend_getPromotionDateRage('F','Code3' ,VacLimit ,#{domainCode}) END AS VacLimitTwoFrom , fn_attend_getPromotionDateRage('F','Code4' ,VacLimit ,#{domainCode}) AS VacLimitLessFrom , fn_attend_getPromotionDateRage('F','Code5' ,VacLimit ,#{domainCode}) AS VacLimit091From , fn_attend_getPromotionDateRage('F','Code6' ,VacLimit ,#{domainCode}) AS VacLimit092From , fn_attend_getPromotionDateRage('F','Code7' ,VacLimit ,#{domainCode}) AS VacLimit021From , fn_attend_getPromotionDateRage('F','Code8' ,VacLimit ,#{domainCode}) AS VacLimit022From FROM (SELECT A.UserCode , A.MultiDisplayName , A.EnterDate , B.MultiDeptName , B.MultiJobPositionName , Tyear , TO_DATE(CONCAT(Tyear,TO_CHAR(TO_DATE(EnterDate,'YYYYMMDD'),'-MM-DD')),'YYYY-MM-DD') AS VacYear , CASE WHEN IsOneYear = 0 THEN ADD_MONTHS(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'), 12) WHEN CFG.CreateMethod = 'J' THEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'-MM-DD'))) ELSE ADD_MONTHS(TO_DATE(CONCAT(#{year},'-01-01'),'YYYY-MM-DD'), 12) END AS VacLimit , (SELECT NVL(SUM(VacDay),0) FROM VM_VACATIONPLAN WHERE UR_Code = #{urCode} AND VacKind='PUBLIC' AND YEAR = Tyear) AS OWNDAYS , (SELECT NVL(SUM(VacDay),0.0) FROM VM_VACATIONPLAN WHERE UR_Code = #{urCode} AND VacKind <> 'PUBLIC' AND VacKind IN (SELECT Code FROM SYS_BASE_CODE WHERE CodeGroup = 'VACATION_TYPE' AND IsUse ='Y' AND DomainID = (SELECT DomainID FROM sys_object_domain WHERE DomainCode = #{domainCode}) AND Reserved1 ='+' AND (Reserved2 is null or Reserved2 ='') ) AND TO_DATE(UseStartDate,'YYYYMMDD') >= Sdate AND TO_DATE(UseEndDate,'YYYYMMDD') <= Edate ) AS EtcOWNDAYS
, (SELECT TO_CHAR(NVL(SUM(CASE WHEN B.CodeID IS NOT NULL THEN VacDay ELSE 0 END) ,0), 'fm90.000') FROM VM_VACATIONINFO A LEFT JOIN sys_base_code B ON B.CodeGroup = 'VACATION_TYPE' AND B.Reserved1 = '+' AND B.IsUse = 'Y' AND A.VacFlag = B.Code AND B.DomainID = (SELECT DomainID FROM sys_object_domain WHERE DomainCode = #{domainCode}) WHERE A.VacYear = (Tyear-1) AND UR_Code = #{urCode} ) AS BfUSEDAYS
, 0 AS BfUSEDAYS
, NVL(F.USEDAYS,0) AS USEDAYS FROM (SELECT UserCode , MultiDisplayName , EnterDate , Tyear , TO_DATE(CONCAT(Tyear,TO_CHAR(TO_DATE(EnterDate,'YYYYMMDD'),'MMDD')),'YYYYMMDD') as Sdate , TO_DATE(CONCAT(Tyear,TO_CHAR(TO_DATE(EnterDate,'YYYYMMDD'),'MMDD')),'YYYYMMDD') + (INTERVAL '1' YEAR) - (INTERVAL '1' DAY ) as Edate , (CASE WHEN TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD') + (INTERVAL '12' MONTH) <= SYSDATE THEN 1 ELSE 0 END) as IsOneYear FROM (SELECT UserCode , MultiDisplayName , REPLACE(EnterDate,'-','') AS EnterDate , CASE WHEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(EnterDate,'-',''), 'YYYYMMDD'), 12),'YYYYMMDD') <= TO_CHAR(SYSDATE,'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR)) END AS Tyear FROM sys_object_user where USERCODE = #{urCode}) a) A LEFT JOIN sys_object_user_basegroup b ON JobType = 'Origin' AND A.UserCode = B.UserCode LEFT JOIN vm_config CFG ON CFG.CompanyCode = B.CompanyCode LEFT JOIN ( SELECT UR_Code , SUM(VacDay) AS USEDAYS FROM ( SELECT UR_CODE, VACDAY FROM VM_VACATIONINFO V LEFT JOIN (SELECT UserCode, MultiDisplayName, EnterDate ,CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') < TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') THEN TO_DATE(CONCAT(EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR )),TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'-MM-DD')),'YYYY-MM-DD') ELSE TO_DATE(CONCAT(EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')),TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'-MM-DD')),'YYYY-MM-DD') END AS VacYearRange FROM sys_object_user where USERCODE = #{urCode}) A ON V.UR_Code = A.UserCode WHERE TO_DATE(V.Sdate,'YYYY-MM-DD') >= A.VacYearRange AND TO_DATE(V.Edate,'YYYY-MM-DD') <= TO_DATE(A.VacYearRange) + (INTERVAL '1' YEAR) - (INTERVAL '1' DAY) AND UR_Code = #{urCode}
AND A.EDATE <= #{termDate}
AND V.VacFlag in (SELECT Code FROM SYS_BASE_CODE WHERE CodeGroup = 'VACATION_TYPE' AND IsUse = 'Y' AND DomainID = (SELECT DomainID FROM sys_object_domain WHERE DomainCode = #{domainCode}) AND Reserved1 = '+' AND (Reserved2 is null or Reserved2 = '')) ) V GROUP BY V.UR_Code ) F ON A.UserCode = F.UR_Code WHERE A.UserCode = #{urCode} ) T ) TBL

