mapper.update:
/* groupware.vacation.updateVacationPlan */

		SELECT
			CONCAT(#{year},'0101') AS "FromDate"
			, CONCAT(#{year},'1231') AS "ToDate"
			, (
				SELECT DeptCode FROM sys_object_user_basegroup WHERE UserCode = #{urCode} AND JobType = 'Origin'
			) AS "DeptCode"
			, (
				SELECT Fn_BaseGetDictionary_S(#{lang},NVL(MultiDeptName, DeptName)) AS DeptName FROM sys_object_user_basegroup WHERE UserCode = #{urCode} AND JobType = 'Origin'
			) AS "DeptName"
			, fn_GetLastVacDay(#{urCode}, #{year}, #{domainCode}) AS "LastVacDay"
		FROM dual


    MERGE INTO VM_VACATIONPLAN G
	USING DUAL ON (G.UR_Code = #{urCode} AND G.YEAR =  #{year} AND G.VACKIND = 'PUBLIC')
	WHEN MATCHED THEN
	UPDATE SET G.VacDay = #{longVacDay}+#{LastVacDay}
		, G.UseStartDate =  #{FromDate}
		, G.UseEndDate = #{ToDate}
		, G.LongVacDay = #{longVacDay}
		, G.LastVacDay = #{LastVacDay}
	WHEN NOT MATCHED THEN 
	INSERT (G.UR_Code, G.YEAR, G.VacDay, G.LongVacDay, G.LastVacDay, G.UseStartDate, G.UseEndDate, G.RegisterCode, G.RegistDate, G.DeptCode, G.DeptName)
	VALUES (#{urCode}, #{year}, #{longVacDay}+#{LastVacDay}
		, #{longVacDay}
		, #{LastVacDay}
		, #{FromDate}
		, #{ToDate}
		, #{registerCode}
		, sysdate
		, #{DeptCode}
		, #{DeptName}
	)

/* groupware.vacation.updateVacationPlanV2 */

	SELECT
		 (select CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN
				EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD'))||TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')
			ELSE
				EXTRACT(YEAR FROM ADD_MONTHS(TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD'), -12 ))||TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')
			END
			from sys_object_user
			where USERCODE = #{urCode}
		) AS "FromDate"
		, (select CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN
				TO_CHAR(ADD_MONTHS(TO_DATE(EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD'))||TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD'),'YYYYMMDD'), 12)-1,'YYYYMMDD')
			ELSE
				TO_CHAR(ADD_MONTHS(TO_DATE(EXTRACT(YEAR FROM ADD_MONTHS(TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD'), -12 ))||TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD'),'YYYYMMDD'), 12)-1,'YYYYMMDD')
			END
			from sys_object_user
			where USERCODE = #{urCode}
		) AS "ToDate"
		, (
			SELECT DeptCode FROM sys_object_user_basegroup WHERE UserCode = #{urCode} AND JobType = 'Origin'
		) AS "DeptCode"
		, (
			SELECT NVL(MultiDeptName, DeptName) AS DeptName FROM sys_object_user_basegroup WHERE UserCode = #{urCode} AND JobType = 'Origin'
		) AS "DeptName"
		,fn_GetLastVacDay(#{urCode}, TO_NUMBER(SUBSTR((select CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN
				EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD'))||TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')
			ELSE
				EXTRACT(YEAR FROM ADD_MONTHS(TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD'), -12 ))||TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')
			END
			from sys_object_user
			where USERCODE = #{urCode}
		),1,4)), #{domainCode}) AS "LastVacDay"
	FROM dual


MERGE INTO VM_VACATIONPLAN G
USING DUAL ON (G.UR_Code = #{urCode} AND G.YEAR = TO_NUMBER(SUBSTR(#{FromDate},1,4)) AND G.VACKIND = 'PUBLIC')
WHEN MATCHED THEN
UPDATE SET G.VacDay = #{longVacDay}+#{LastVacDay}
	, G.UseStartDate =  #{FromDate}
	, G.UseEndDate = #{ToDate}
	, G.LongVacDay = #{longVacDay}
	, G.LastVacDay = #{LastVacDay}
WHEN NOT MATCHED THEN
INSERT (G.UR_Code, G.YEAR, G.VacDay, G.LongVacDay, G.LastVacDay, G.UseStartDate, G.UseEndDate, G.RegisterCode, G.RegistDate, G.DeptCode, G.DeptName)
VALUES (#{urCode}, TO_NUMBER(SUBSTR(#{FromDate},1,4)),  #{longVacDay}+#{LastVacDay}
, #{longVacDay}
, #{LastVacDay}
, #{FromDate}
, #{ToDate}
, #{registerCode}
, sysdate
, #{DeptCode}
, #{DeptName}
)

mapper.select:
/* groupware.vacation.selectVacationListCntByMonth */ SELECT count(u.UserCode) FROM sys_object_user u JOIN sys_object_user_basegroup ub ON u.UserCode = ub.UserCode and JobType = 'Origin' JOIN sys_object_group g ON ub.DeptCode = g.GroupCode and ';'||GroupPath LIKE '%;'||#{deptCode} ||';%' and ';'||GroupPath LIKE '%;'||#{schDeptID} ||';%' WHERE ( substr(u.EnterDate,0,4) <= #{year} AND substr(nvl(u.RetireDate,'2099'),0,4) >= #{year} )
AND ub.Companycode = #{domainCode}
AND u.RetireDate is null AND ub.DeptCode NOT LIKE '%RetireDept'
AND u.RetireDate is not null
AND ub.DeptName LIKE '%'|| #{schTxt}|| '%'
AND u.DisplayName LIKE '%'|| #{schTxt}|| '%'

SELECT COUNT(UserCode) FROM ( SELECT UserCode FROM ( SELECT A.UserCode , Fn_BaseGetDictionary_S(#{lang}, B.MultiDisplayName) AS DisplayName FROM sys_object_user_basegroup A LEFT JOIN sys_object_user B ON A.UserCode = B.UserCode
INNER JOIN ( SELECT GroupCode FROM sys_object_group WHERE CONCAT(';',GroupPath) LIKE '%;' || #{deptCode} || ';%' ) C ON A.DeptCode = C.GroupCode
INNER JOIN ( SELECT GroupCode FROM sys_object_group WHERE CONCAT(';',GroupPath) LIKE '%;' || #{schDeptID} || ';%' ) C ON A.DeptCode = C.GroupCode
WHERE A.JobType = 'Origin' AND ( EXTRACT(YEAR FROM TO_DATE(B.EnterDate)) <= #{year} AND EXTRACT(YEAR FROM TO_DATE(CASE WHEN B.RetireDate = '' THEN '2999-01-01' ELSE B.RetireDate END)) >= #{year} )
AND A.Companycode = #{domainCode}
AND B.RetireDate IS NULL AND A.DeptCode NOT LIKE '%RetireDept'
AND (B.RetireDate != '' OR B.RetireDate IS NOT NULL) AND EXTRACT(YEAR FROM TO_DATE(B.RetireDate)) = #{year}
) A
WHERE DisplayName LIKE '%' || #{displayName} || '%'
) A

/* groupware.vacation.selectVacationMessageReadList */
SELECT UserCode AS "UserCode" , DisplayName AS "DisplayName" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , EnterDate AS "EnterDate" , RetireDate AS "RetireDate" , TO_CHAR(B.ReadDate, 'YYYY.MM.DD HH24:MI:SS') AS "ReadDate" FROM (SELECT A.UserCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , A.EnterDate , A.RetireDate , B.DeptCode , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , B.CompanyCode FROM sys_object_user A LEFT JOIN (SELECT UserCode, DeptCode, MultiDeptName, MultiJobPositionName, CompanyCode FROM sys_object_user_basegroup WHERE JobType = 'Origin' ) B ON A.UserCode = B.UserCode WHERE B.CompanyCode = #{domainCode}
) A LEFT JOIN (SELECT MessageID, ReaderCode, ReadDate FROM vm_message_reader WHERE YEAR = #{year} AND MessageID = #{messageId}) B ON A.UserCode = B.ReaderCode WHERE 1 = 1
AND ReadDate IS NOT NULL
AND ReadDate IS NULL
AND A.RetireDate IS NULL AND A.DeptCode NOT LIKE '%RetireDept'
AND A.RetireDate IS NOT NULL AND EXTRACT(YEAR FROM TO_DATE(A.RetireDate, 'YYYY-MM-DD')) = #{year}
AND DeptName LIKE '%' || #{schTxt} || '%'
AND DisplayName LIKE '%' || #{schTxt} || '%'
JobPositionName DisplayName EnterDate RetireDate ReadDate DeptName
ASC DESC

/* groupware.vacation.selectVacationMessageReadListCnt */ SELECT COUNT(UserCode) as "cnt" FROM (SELECT A.UserCode, A.DisplayName, A.EnterDate, A.RetireDate, B.DeptCode, B.DeptName, B.JobPositionName FROM sys_object_user A LEFT JOIN (SELECT UserCode, DeptCode, DeptName, JobPositionName, CompanyCode FROM sys_object_user_basegroup WHERE JobType = 'Origin' ) B ON A.UserCode = B.UserCode WHERE B.CompanyCode = #{domainCode}
) A LEFT JOIN (SELECT MessageID, ReaderCode, ReadDate FROM vm_message_reader WHERE YEAR = #{year} AND MessageID = #{messageId}) B ON A.UserCode = B.ReaderCode WHERE 1 = 1
AND ReadDate IS NOT NULL
AND ReadDate IS NULL
AND A.RetireDate IS NULL AND A.DeptCode NOT LIKE '%RetireDept'
AND A.RetireDate IS NOT NULL AND EXTRACT(YEAR FROM TO_DATE(A.RetireDate, 'YYYY-MM-DD')) = #{year}
AND DeptName LIKE '%' || #{schTxt} || '%'
AND DisplayName LIKE '%' || #{schTxt} || '%'

/* groupware.vacation.selectVacationUsePlanList */
SELECT DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , UserCode AS "UserCode" , DisplayName AS "DisplayName" , EnterDate AS "EnterDate" , TargetYear AS "TargetYear" , IsOneYear AS "IsOneYear" , #{year} AS "YEAR" , VacDayPublic+VacDayExtra AS "OWNDAYS" , USEDAYS AS "USEDAYS" , (VacDayPublic+VacDayExtra) - USEDAYS AS "REMINDDAYS" , VACPLAN AS "VACPLAN" FROM ( SELECT A.DeptName , A.JobPositionName , A.UserCode , A.DisplayName , A.EnterDate , A.TargetYear , IsOneYear , JobPositionSortKey , SortKey , CAST(NVL(B.VacDay, 0.0) AS FLOAT) AS VacDayPublic , (SELECT CAST(NVL(SUM(E.VacDay), 0.0) AS FLOAT) FROM vm_vacationplan E WHERE E.VacKind <> 'PUBLIC' and E.UseStartDate >= B.UseStartDate and E.UseEndDate <= B.UseEndDate and E.UR_Code = B.UR_Code ) AS VacDayExtra , (SELECT CAST(NVL(SUM(VacDay), 0.0) AS FLOAT) FROM vm_vacationinfo WHERE A.UserCode = UR_Code AND TO_DATE(Sdate, 'YYYY-MM-DD') >= TO_DATE(B.UseStartDate, 'YYYYMMDD') AND TO_DATE(Edate, 'YYYY-MM-DD') <= TO_DATE(B.UseEndDate, 'YYYYMMDD') AND VacFlag IN (SELECT code FROM sys_base_code cd WHERE cd.domainid= #{domainID} AND cd.CodeGroup = 'VACATION_TYPE' AND NVL(Reserved1,'N') = '+' ) ) AS USEDAYS , C.VACPLAN FROM ( SELECT * FROM ( SELECT U.UserCode , U.SortKey , Fn_BaseGetDictionary_S(#{lang}, U.MultiDisplayName) AS DisplayName , U.EnterDate , U.RetireDate , UB.CompanyCode , UB.DeptCode , Fn_BaseGetDictionary_S(#{lang}, NVL(UB.MultiDeptName, UB.DeptName)) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, UB.MultiJobPositionName) AS JobPositionName , UB.JobPositionSortKey , CASE WHEN CFG.CreateMethod = 'J' THEN CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR )) END ELSE CASE WHEN TO_DATE(REPLACE(U.EnterDate,'-',''),'YYYYMMDD') + (INTERVAL '12' MONTH) <= SYSDATE THEN EXTRACT(YEAR FROM TO_DATE(#{year},'YYYY')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR )) END END AS TargetYear , (CASE WHEN TO_CHAR(TO_DATE(REPLACE(U.EnterDate,'-',''),'YYYYMMDD') + (INTERVAL '12' MONTH), 'YYYYMMDD') <= TO_CHAR(SYSDATE, 'YYYYMMDD') -- 1 THEN 1 ELSE 0 END ) as IsOneYear FROM sys_object_user U LEFT JOIN vm_config CFG ON CFG.CompanyCode = #{domainCode} LEFT JOIN sys_object_user_basegroup UB ON U.UserCode = UB.UserCode AND UB.JobType = 'Origin' ) U WHERE U.EnterDate IS NOT NULL AND LENGTH(REPLACE(U.EnterDate,'-','')) = 8
AND U.IsOneYear = 1
AND U.IsOneYear = 0
AND U.CompanyCode = #{domainCode}
AND U.UserCode = #{urCode}
AND U.DeptName LIKE '%' || #{schTxt} || '%'
AND U.DisplayName LIKE '%' || #{schTxt} || '%'
AND (U.RetireDate = '' OR U.RetireDate IS NULL) AND U.DeptCode NOT LIKE '%RetireDept'
AND (U.RetireDate != '' OR U.RetireDate IS NOT NULL) AND EXTRACT(YEAR FROM TO_DATE(U.RetireDate)) = #{year}
) A LEFT JOIN vm_vacationplan B ON A.UserCode = B.UR_Code AND B.YEAR = A.TargetYear AND B.VacKind = 'PUBLIC' LEFT JOIN vm_useage_plan C ON A.UserCode = C.UR_Code AND C.YEAR = A.TargetYear
JobPositionSortKey ASC, A.SortKey ASC
JobPositionName DisplayName DeptName
ASC DESC
) TBL

