/* groupware.vacation.getVacationListByUse */
SELECT a.VacationInfoID "VacationInfoID" , A.UR_Code "UR_Code" , VacYear "VacYear" , VacDate "VacDate" , VacFlag "VacFlag" , VacOffFlag "VacOffFlag" , Fn_BaseGetDictionary_S(#{lang}, b.MultiDisplayName) AS "DisplayName" , Fn_BaseGetDictionary_S(#{lang}, nvl(E.DeptName, b.MultiDeptName)) AS "DeptName" , Fn_BaseGetDictionary_S(#{lang}, b.MultiJobPositionName) AS "JobPositionName" , Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) AS "VacFlagName" , TO_CHAR(b.VacDay, 'fm90.0') AS "VacDay" , Reason "Reason" , WorkItemID "WorkItemID" , ProcessID "ProcessID" FROM vm_vacationinfo A JOIN (SELECT vm.UR_Code , ad.dayList VacDate ,u.MultiDisplayName ,bg.MultiDeptName ,bg.MultiJobPositionName ,SUM(fn_attend_calVacDay(VacFlag, VacOffFlag, GUBUN)) VacDay ,MAX(case when Gubun IN ('VACATION_APPLY','VACATION_PUBLIC') then VacationInfoID END) vacationinfoid FROM vm_vacationinfo vm JOIN attend_daylist ad ON ad.dayList BETWEEN vm.Sdate AND vm.Edate LEFT JOIN sys_object_user u ON vm.UR_Code = u.UserCode LEFT JOIN sys_object_user_basegroup bg ON u.UserCode = bg.UserCode AND JobType = 'Origin' WHERE vacYear =#{year} AND bg.Companycode = #{domainCode}
AND DeptCode = #{DEPTID}
AND DeptName LIKE '%'|| #{schTxt}|| '%'
AND DisplayName LIKE '%'|| #{schTxt}|| '%'
AND DisplayName LIKE '%'|| #{schUrName}|| '%'
AND ad.dayList between to_date( #{sDate},'YYYYMMDD') AND to_date(#{eDate},'YYYYMMDD')
AND vacFlag = #{vacFlag}
GROUP BY vm.Ur_Code, ad.dayList,u.MultiDisplayName,bg.MultiDeptName,bg.MultiJobPositionName HAVING SUM(fn_attend_calVacDay(VacFlag, VacOffFlag, GUBUN))>0 ) b ON A.vacationinfoid = b.vacationinfoid LEFT JOIN vm_vacationplan E ON A.UR_Code = E.UR_Code and E.year = a.VacYear LEFT JOIN sys_base_code D ON A.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} WHERE 1 =1
VacDate DESC
DisplayName VacFlagName VacYear VacDate VacDay DeptName VacDate
ASC DESC

