AND vacFlag = #{vacFlag}
GROUP BY vm.Ur_Code, ad.dayList HAVING SUM(fn_attend_calVacDay(VacFlag, VacOffFlag, GUBUN))>0 ) b ON A.vacationinfoid = b.vacationinfoid WHERE 1 =1
AND concat(#{year},#{month}) BETWEEN TO_CHAR(SDate,'YYYYMM') AND TO_CHAR(EDate,'YYYYMM')
AND vacFlag = #{vacFlag}

