SELECT VacationInfoID AS "VacationInfoID" , UR_Code AS "UR_Code" , AppDate AS "AppDate" , EndDate AS "EndDate" , VacYear AS "VacYear" , Sdate AS "Sdate" , Edate AS "Edate" , VacFlag AS "VacFlag" , VacOffFlag AS "VacOffFlag" , DisplayName AS "DisplayName" , DeptName AS "DeptName" , UpDeptName AS "UpDeptName" , JobPositionName AS "JobPositionName" , JobPositionSortKey AS "JobPositionSortKey" , JobLevelSortKey AS "JobLevelSortKey" , JobTitleSortKey AS "JobTitleSortKey" , VacFlagName AS "VacFlagName" , VacDay AS "VacDay" , Reason AS "Reason" , WorkItemID AS "WorkItemID" , ProcessID AS "ProcessID" , Gubun AS "Gubun" , GubunName AS "GubunName" , VacText AS "VacText" FROM ( SELECT vm.VacationInfoID , vm.UR_Code , nvl(j.StartDate, APPDATE) AppDate , nvl(j.EndDate, vm.EndDate) EndDate , VacYear , SDate whSDate , EDate whEDate , Sdate|| ' ('|| CASE TO_CHAR(TO_DATE(Sdate,'YYYY-MM-DD'),'D') WHEN '2' THEN 'Mon' WHEN '3' THEN 'Tue' WHEN '4' THEN 'Wed' WHEN '5' THEN 'Thu' WHEN '6' THEN 'Fri' WHEN '7' THEN 'Sat' ELSE 'Sun' END||')' as Sdate , Edate|| ' ('|| CASE TO_CHAR(TO_DATE(Edate,'YYYY-MM-DD'),'D') WHEN '2' THEN 'Mon' WHEN '3' THEN 'Tue' WHEN '4' THEN 'Wed' WHEN '5' THEN 'Thu' WHEN '6' THEN 'Fri' WHEN '7' THEN 'Sat' ELSE 'Sun' END||')' as Edate , VacFlag , VacOffFlag , Fn_BaseGetDictionary_S(#{lang}, u.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, nvl(E.DeptName, bg.MultiDeptName)) AS DeptName , (SELECT Fn_BaseGetDictionary_S(#{lang}, ug.MultiDisplayName) FROM sys_object_group g JOIN sys_object_group ug ON ug.GroupType ='Dept' AND ug.GroupCode = g.MemberOf WHERE g.GroupType ='Dept' AND g.GroupCode = nvl(E.DeptCode, bg.DeptCode)) AS UpDeptName , Fn_BaseGetDictionary_S(#{lang}, bg.MultiJobPositionName) AS JobPositionName , Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) AS VacFlagName , cast(cast(TO_CHAR(vm.VacDay, 'fm90.000') as DECIMAL(9,3)) as float) AS VacDay , Reason , WorkItemID , vm.ProcessID , vm.Gubun , Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) AS GubunName , CASE WHEN GUBUN in ('VACATION_CANCEL' ,'VACATION_PUBLIC' ,'VACATION_PUBLIC_CANCEL' ) THEN '('|| Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName)|| ')' ELSE '' END|| Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) AS VacText , bg.Companycode , bg.DeptCode , bg.JobPositionSortKey , bg.JobLevelSortKey , bg.JobTitleSortKey FROM vm_vacationinfo vm LEFT JOIN sys_object_user u ON vm.UR_Code = u.UserCode LEFT JOIN sys_object_user_basegroup bg ON u.UserCode = bg.UserCode AND JobType = 'Origin' LEFT JOIN vm_vacationplan E ON vm.UR_Code = E.UR_Code and vm.VacYear = e.year LEFT JOIN sys_base_code C ON vm.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID =0 LEFT JOIN sys_base_code D ON vm.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} LEFT JOIN jwf_process j ON vm.processid = j.processid ) TBL WHERE 1=1 AND vacYear =#{year} AND Companycode = #{domainCode}
