/* groupware.vacation.selectVacationExtraList */
SELECT UR_Code AS "UR_Code" , DisplayName AS "DisplayName" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , JobPositionSortKey AS "JobPositionSortKey" , JobLevelSortKey AS "JobLevelSortKey" , JobTitleSortKey AS "JobTitleSortKey" , CodeName AS "ExtVacName" , IsUse AS "IsUse" , Year as "ExtVacYear" , UseStartDate as "ExtSdate" , UseEndDate as "ExtEdate" , VacKind as "ExtVacType" , ExtVacDay AS "ExtVacDay" , VmComment as "ExtReason" , RegisterName AS "RegisterName" , RegisterCode AS "RegisterCode" , RegistDate AS "RegistDate" , ExpDate AS "ExpDate" , CAST(NVL(ExtUseVacDay,0.0) AS FLOAT) AS "ExtUseVacDay" , CAST(ExtVacDay - NVL(ExtUseVacDay,0.0) as float) AS "ExtRemainVacDay" FROM (
SELECT A.UR_Code , B.DisplayName , Fn_BaseGetDictionary_S(#{lang}, NVL(B.MultiDeptName, A.DeptName)) AS DeptName , B.JobPositionName , B.JobPositionSortKey , B.JobLevelSortKey , B.JobTitleSortKey , C.CodeName , C.IsUse , Year , UseStartDate , UseEndDate , VacKind , cast(cast(A.VacDay as DECIMAL(9,3)) as float) AS ExtVacDay , VmComment , CASE WHEN RegisterName is not null THEN RegisterName ELSE A.RegisterCode END AS RegisterName , A.RegisterCode , TO_CHAR(A.RegistDate, 'YYYY-MM-DD') AS RegistDate , TO_CHAR(TO_DATE(A.UseStartDate), 'YYYY.MM.DD') || ' ~ ' || TO_CHAR(TO_DATE(A.UseEndDate), 'YYYY.MM.DD') AS ExpDate , (SELECT SUM(NVL(VacDay,0.0)) FROM vm_vacationinfo_day WHERE A.UR_Code = UR_Code AND TRIM(VacFlag) = A.VacKind AND TO_DATE(VacDate,'YYYY-MM-DD') between TO_DATE(A.UseStartDate,'YYYY-MM-DD') and TO_DATE(A.UseEndDate,'YYYY-MM-DD') ) AS ExtUseVacDay , B.CompanyCode FROM vm_vacationplan A LEFT JOIN ( SELECT A.UserCode , B.CompanyCode , B.MultiDeptName , B.JobPositionSortKey , B.JobLevelSortKey , B.JobTitleSortKey , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName FROM sys_object_user A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' ) B ON A.UR_Code = B.UserCode LEFT JOIN (SELECT B.Code , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) ELSE B.CodeName END AS CodeName , CASE WHEN A.IsUse = 'N' OR B.IsUse = 'N' THEN 'N' ELSE 'Y' END AS IsUse FROM (select * from sys_base_code WHERE CodeGroup = 'VACATION_KIND' AND DomainID = 0 ) A, (select * from sys_base_code where DomainID = #{domainID} AND CodeGroup = 'VACATION_TYPE' ) B WHERE A.CodeID = B.ReservedInt ) C ON A.VacKind = C.Code LEFT JOIN (SELECT UserCode ,Fn_BaseGetDictionary_S(#{lang}, MultiDisplayName) AS RegisterName FROM sys_object_user ) E ON A.RegisterCode = E.UserCode ) TBL WHERE Year = #{year} AND VacKind <> 'PUBLIC'
AND CompanyCode = #{domainCode}
AND VacKind = #{vacKind}
AND UseStartDate = #{sDate}
AND UseEndDate = #{eDate}
