JobLevelSortKey ASC
JobPositionSortKey ASC
DisplayName ASC
, VacKind, RegistDate ASC
"DeptName" "DisplayName" "ExtVacName" "ExtVacDay" "RegisterName" "RegistDate" "ExpDate" "ExtUseVacDay" "ExtRemainVacDay" "IsUse" "ExtReason" VacKind, RegistDate
ASC DESC

/* groupware.vacation.selectVacationManageListCnt */ SELECT count(VacationInfoID) FROM vm_vacationinfo A LEFT JOIN sys_object_user U ON A.UR_Code = u.UserCode LEFT JOIN sys_object_user_basegroup B ON A.UR_Code = B.UserCode AND JobType = 'Origin' WHERE #{year} BETWEEN EXTRACT(YEAR FROM TO_DATE(a.Sdate)) AND EXTRACT(YEAR FROM TO_DATE(a.Sdate)) AND (GUBUN ='VACATION_PUBLIC' OR GUBUN ='VACATION_PUBLIC_CANCEL')
AND A.VacationInfoID = #{vacationInfoID}
AND B.Companycode = #{domainCode}
AND DeptName LIKE '%' || #{schTxt} || '%'
AND DisplayName LIKE '%' || #{schTxt} || '%'

