/* groupware.vacation.selectVacationListByKind */
SELECT UR_Code AS "UR_Code" , DisplayName AS "DisplayName" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , JobPositionSortKey AS "JobPositionSortKey" , JobLevelSortKey AS "JobLevelSortKey" , JobTitleSortKey AS "JobTitleSortKey" , VacName AS "VacName" , Year as "VacYear" , UseStartDate as "Sdate" , UseEndDate as "Edate" , VacKind AS "VacKind" , cast(NVL(VacDay,0.0) as float) AS "VacDay" , VmComment as "Reason" , RegisterName AS "RegisterName" , RegisterCode AS "RegisterCode" , RegistDate AS "RegistDate" , ExpDate AS "ExpDate" , cast(NVL(UseVacDay,0.0) as float) AS "UseVacDay" , cast(VacDay-NVL(UseVacDay,0.0) as float) AS "RemainVacDay" , SortKey AS "SortKey" FROM (
SELECT UR_CODE, DisplayName, DeptName, JobPositionName, JobPositionSortKey, JobLevelSortKey, JobTitleSortKey, VacName , YEAR, USESTARTDATE, USEENDDATE, VACKIND, VmComment , VacDay , RegisterName, RegisterCode, RegistDate, ExpDate, SortKey, COMPANYCODE , CASE WHEN VACKIND = 'PUBLIC' THEN ( select SUM(NVL(VACDAY,0.0)) from VM_VACATIONINFO VI left join (SELECT B.Code , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) ELSE B.CodeName END AS CodeName ,A.Code as GroupCode , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) ELSE A.CodeName END AS GroupCodeName ,B.SortKey ,B.CodeID FROM (select * from sys_base_code WHERE IsUse = 'Y' AND DomainID = 0 ) A, sys_base_code B WHERE A.CodeID = B.ReservedInt AND B.IsUse = 'Y' AND B.DomainID = #{domainID}) C ON VI.VACFLAG = C.CODE WHERE TO_DATE(SDATE,'YYYY-MM-DD') >= TO_DATE(USESTARTDATE,'YYYYMMDD') AND TO_DATE(EDATE,'YYYY-MM-DD') <= TO_DATE(USEENDDATE,'YYYYMMDD') AND VI.UR_CODE = T.UR_CODE AND GroupCode = 'PUBLIC' ) ELSE (select SUM(NVL(VACDAY,0.0)) from VM_VACATIONINFO VI where VI.UR_CODE = T.UR_CODE and TO_DATE(SDATE,'YYYY-MM-DD') >= TO_DATE(USESTARTDATE,'YYYYMMDD') AND TO_DATE(EDATE,'YYYY-MM-DD') <= TO_DATE(USEENDDATE,'YYYYMMDD') and VACFLAG = T.VACKIND ) END AS UseVacDay FROM ( SELECT A.UR_Code , B.DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , B.JobPositionName , B.JobPositionSortKey , B.JobLevelSortKey , B.JobTitleSortKey , CASE WHEN VACKIND = 'PUBLIC' THEN (select Fn_BaseGetDictionary_S(#{lang}, MULTICODENAME) from sys_base_code WHERE DomainID = 0 AND CODEGROUP='VACATION_KIND' AND CODE = VACKIND) ELSE (select Fn_BaseGetDictionary_S(#{lang}, MULTICODENAME) from sys_base_code WHERE DomainID = #{domainID} AND CODEGROUP='VACATION_TYPE' AND CODE = VACKIND) END AS VacName , A.Year , UseStartDate , UseEndDate , VacKind , (select VacDay from VM_VACATIONPLAN where UR_CODE = A.UR_CODE and YEAR = A.YEAR and VACKIND = A.VACKIND and USESTARTDATE = A.USESTARTDATE and USEENDDATE = A.USEENDDATE) AS VacDay , MAX(VmComment) AS VmComment , CASE WHEN RegisterName is not null THEN RegisterName ELSE A.RegisterCode END AS RegisterName , A.RegisterCode AS RegisterCode , TO_CHAR(MAX(A.RegistDate), 'YYYY-MM-DD') AS RegistDate , TO_CHAR(TO_DATE(A.UseStartDate, 'YYYY-MM-DD'), 'YYYY.MM.DD') || ' ~ ' || TO_CHAR(TO_DATE(A.UseEndDate, 'YYYY-MM-DD'), 'YYYY.MM.DD') AS ExpDate , MAX(C.SortKey) AS SortKey , B.CompanyCode FROM vm_vacationplan A LEFT JOIN (SELECT A.UserCode , B.CompanyCode , B.MultiDeptName , B.JobPositionSortKey , B.JobLevelSortKey , B.JobTitleSortKey , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName FROM sys_object_user A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' ) B ON A.UR_Code = B.UserCode LEFT JOIN (SELECT B.Code , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) ELSE B.CodeName END AS CodeName , A.Code as GroupCode , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) ELSE A.CodeName END AS GroupCodeName , B.SortKey , B.CodeID FROM (select * from sys_base_code WHERE IsUse = 'Y' AND DomainID = 0) A, sys_base_code B WHERE A.CodeID = B.ReservedInt AND B.IsUse = 'Y' AND B.DomainID = #{domainID}) C ON A.VacKind = C.GroupCode LEFT JOIN (SELECT UserCode, Fn_BaseGetDictionary_S(#{lang}, MultiDisplayName) AS RegisterName FROM sys_object_user) E ON A.RegisterCode = E.UserCode GROUP BY A.UR_Code , B.DisplayName, B.MultiDeptName, B.JobPositionName , A.Year, UseStartDate, UseEndDate, VacKind, RegisterName, A.RegisterCode , B.CompanyCode, C.GroupCode, B.JobPositionSortKey, B.JobLevelSortKey, B.JobTitleSortKey ) T
) TBL WHERE 1=1 AND Year = #{year} AND VacName is not null
AND Companycode = #{domainCode}
AND VacKind = #{vacKind}
AND UseStartDate = #{sDate}
AND UseEndDate = #{eDate}
AND UR_Code = #{urCode}
AND CodeID = #{codeID}
AND DeptName LIKE '%' || #{schTxt} || '%'
AND UR_Code = #{schTxt}
AND DisplayName LIKE '%' || #{schTxt} || '%'
JobTitleSortKey ASC
JobLevelSortKey ASC
JobPositionSortKey ASC
DisplayName ASC
, SortKey, VacKind, RegistDate ASC

/* groupware.vacation.selectVacationListByKindAll */ SELECT Year AS "Year", VacKind as "VacKind", Fn_BaseGetDictionary_S('', VacName) "VacName" ,VacDay as "VacDay" ,VacDayUse as "VacDayUse" ,VacDayProc as "VacDayProc" ,cast(VacDay-VacDayUse as float) AS "RemainVacDay" ,A.UseStartDate "Sdate" ,A.UseEndDate "Edate" ,to_char(to_Date(A.UseStartDate, 'yyyymmdd'),'yyyy.mm.dd')||' ~ '||to_char(to_Date(A.UseEndDate, 'yyyymmdd'),'yyyy.mm.dd') AS "ExpDate" ,Reason "Reason" ,case when to_char(sysdate,'yyyymmdd') BETWEEN UseStartDate AND UseEndDate then 'Y' ELSE 'N' END "CurYear" FROM ( SELECT YEAR, VacKind , case when p.vackind = 'PUBLIC' then k.MultiCodeName ELSE t.MultiCodeName END VacName , case when p.vackind = 'PUBLIC' then k.IsUse ELSE t.IsUse END IsUse , UseStartDate, UseEndDate , CAST(VacDay AS FLOAT) VacDay ,(SELECT nvl(SUM(u.VacDay), 0) FROM vm_vacationinfo_day u LEFT JOIN SYS_BASE_CODE k ON u.VacFlag = k.CODE AND k.CodeGroup = 'VACATION_TYPE' AND k.IsUse ='Y' AND k.DomainID = #{domainID} AND k.Reserved1 ='+' LEFT JOIN SYS_BASE_CODE t ON k.ReservedInt = t.CodeID WHERE p.ur_code = u.UR_Code AND u.VacDate BETWEEN TO_DATE(p.UseStartDate, 'YYYY-MM-DD') AND TO_DATE(p.UseEndDate , 'YYYY-MM-DD') AND (p.VacKind = 'PUBLIC' AND p.VacKind = t.Code OR p.vackind != 'PUBLIC' AND p.VacKind = K.Code) ) VacDayUse ,(SELECT nvl(SUM(u.VacDay), 0) FROM vm_vacationinfo_process u LEFT JOIN SYS_BASE_CODE k ON u.VacFlag = k.CODE AND k.CodeGroup = 'VACATION_TYPE' AND k.IsUse ='Y' AND k.DomainID = #{domainID} AND k.Reserved1 ='+' LEFT JOIN SYS_BASE_CODE t ON k.ReservedInt = t.CodeID WHERE p.ur_code = u.UR_Code AND u.Sdate >= TO_DATE(p.UseStartDate, 'YYYY-MM-DD') AND u.EDate<= TO_DATE(P.UseEndDate , 'YYYY-MM-DD') AND (p.VacKind = 'PUBLIC' AND p.VacKind = t.Code OR p.vackind != 'PUBLIC' AND p.VacKind = K.Code) AND Gubun = 'VACATION_APPLY' ) VacDayProc , p.VmComment Reason FROM vm_vacationplan p LEFT JOIN sys_base_code k ON p.vackind = 'PUBLIC' AND k.CodeGroup = 'VACATION_KIND' AND k.DOMAINID=0 AND k.code = vackind LEFT JOIN sys_base_code t ON p.vackind != 'PUBLIC' AND t.CodeGroup = 'VACATION_TYPE' AND t.DOMAINID in (0,#{domainID}) AND t.code = vackind AND t.IsUse = 'Y' WHERE p.ur_code = #{schTxt}
AND to_char(sysdate,'yyyymmdd') BETWEEN UseStartDate AND UseEndDate -- AND VacKind = 'PUBLIC'
AND ( UseEndDate BETWEEN to_char(add_months(sysdate, -3) ,'yyyymmdd') and to_char(sysdate-1 ,'yyyymmdd') -- 3 OR to_char(sysdate,'yyyymmdd') BETWEEN UseStartDate AND UseEndDate -- OR UseStartDate > to_char(sysdate,'yyyymmdd') -- all )
) A WHERE a.IsUse = 'Y' ORDER BY YEAR desc, VacKind, UseStartDate, UseEndDate

