/* groupware.vacation.selectVacationListByKindV2 */
SELECT UR_Code AS "UR_Code" , DisplayName AS "DisplayName" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , VacName AS "VacName" , VacYear as "VacYear" , Sdate as "Sdate" , Edate as "Edate" , VacKind AS "VacKind" , VacDay AS "VacDay" , Reason as "Reason" , RegisterName AS "RegisterName" , RegisterCode AS "RegisterCode" , RegistDate AS "RegistDate" , ExpDate AS "ExpDate" , UseVacDay AS "UseVacDay" , cast(VacDay-UseVacDay as float) AS "RemainVacDay" , TargetYear AS "TargetYear" FROM ( SELECT UR_Code AS UR_Code , DisplayName AS DisplayName , DeptName AS DeptName , JobPositionName AS JobPositionName , VacName AS VacName , Year as VacYear , UseStartDate as Sdate , UseEndDate as Edate , VacKind AS VacKind , MAX(VacDay) AS VacDay , VmComment as Reason , RegisterName AS RegisterName , RegisterCode AS RegisterCode , RegistDate AS RegistDate , ExpDate AS ExpDate , SUM(UseVacDay) AS UseVacDay , TargetYear AS TargetYear FROM ( SELECT A.UR_Code , B.DisplayName , Fn_BaseGetDictionary_S(#{lang}, NVL(A.DeptName, B.MultiDeptName)) AS DeptName , B.JobPositionName , GroupCodeName AS VacName , Year , UseStartDate , UseEndDate , VacKind , cast(cast(A.VacDay as DECIMAL(9,3)) as float) AS VacDay , VmComment , CASE WHEN RegisterName is not null THEN RegisterName ELSE A.RegisterCode END AS RegisterName , A.RegisterCode AS RegisterCode , TO_CHAR(A.RegistDate, 'YYYY-MM-DD') AS RegistDate , TO_CHAR(TO_DATE(A.UseStartDate,'YYYYMMDD'), 'YYYY.MM.DD') || ' ~ ' || TO_CHAR(TO_DATE(A.UseEndDate,'YYYYMMDD'), 'YYYY.MM.DD') AS ExpDate , (SELECT cast(NVL(SUM(NVL(VacDay,0.0)),0.0) as float) FROM vm_vacationinfo V ,(SELECT B.Code , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) ELSE B.CodeName END AS CodeName , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) ELSE A.CodeName END AS GroupCodeName ,A.Code as GroupCode ,B.SortKey ,B.CodeID FROM (select * from sys_base_code WHERE IsUse = 'Y' AND DomainID = 0 ) A, sys_base_code B WHERE A.CodeID = B.ReservedInt AND B.IsUse = 'Y' AND B.Reserved1 = '+'
AND B.DomainID = #{domainID} ) C where V.UR_Code = A.UR_Code AND V.VacFlag = C.Code AND C.GroupCode = 'PUBLIC' AND TO_DATE(V.Sdate,'YYYY-MM-DD') >= TO_DATE(A.UseStartDate,'YYYYMMDD') AND TO_DATE(V.Edate,'YYYY-MM-DD') <= TO_DATE(A.UseEndDate,'YYYYMMDD') ) AS UseVacDay , TargetYear , B.CompanyCode FROM ( SELECT A.UserCode , B.CompanyCode , B.MultiDeptName , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , A.TargetYear FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR)) END AS TargetYear from sys_object_user where EnterDate is not null) A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' ) B LEFT JOIN (select * from vm_vacationplan where VacKind='PUBLIC') A ON A.UR_Code = B.UserCode and A.YEAR = B.TargetYear LEFT JOIN (SELECT CASE WHEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) ELSE A.CodeName END AS GroupCodeName ,A.Code as GroupCode FROM (select * from sys_base_code WHERE IsUse = 'Y' AND CODEGROUP='VACATION_KIND' AND DomainID = 0 ) A ) C ON A.VacKind = C.GroupCode LEFT JOIN (SELECT UserCode ,Fn_BaseGetDictionary_S(#{lang}, MultiDisplayName) AS RegisterName FROM sys_object_user ) E ON A.RegisterCode = E.UserCode LEFT JOIN (select Code, Fn_BaseGetDictionary_S(#{lang}, NVL(MultiCodeName, CodeName)) AS CodeName, SortKey from sys_base_code where IsUse = 'Y' and DomainID = #{domainID} and CodeGroup = 'VACATION_TYPE') G ON G.Code = A.VacKind ) TB WHERE 1=1 AND VacKind = 'PUBLIC'
AND Companycode = #{domainCode}
AND VacKind = #{vacKind}
AND UseStartDate = #{sDate}
AND UseEndDate = #{eDate}
AND UR_Code = #{urCode}
AND CodeID = #{codeID}
AND DeptName LIKE '%' || #{schTxt} || '%'
AND UR_Code = #{schTxt}
AND DisplayName LIKE '%' || #{schTxt} || '%'
GROUP BY UR_Code, DisplayName, DeptName, JobPositionName, VacName, Year, UseStartDate, UseEndDate, VacKind, VmComment, RegisterName, RegisterCode, RegistDate, ExpDate, TargetYear
DisplayName, VacKind, RegistDate ASC
DisplayName ExtVacName RegistDate (VacDay-UseVacDay) ExtUseVacDay ExpDate ExtVacDay ExtReason RegisterName DeptName DisplayName, VacKind, RegistDate
ASC DESC
)TBL

/* groupware.vacation.selectVacationListByKindV3 */
SELECT UR_Code AS "UR_Code" , DisplayName AS "DisplayName" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , VacName AS "VacName" , Year as "VacYear" , UseStartDate as "Sdate" , UseEndDate as "Edate" , VacKind AS "VacKind" , VacDay AS "VacDay" , VmComment as "Reason" , RegisterName AS "RegisterName" , RegisterCode AS "RegisterCode" , RegistDate AS "RegistDate" , ExpDate AS "ExpDate" , UseVacDay AS "UseVacDay" , cast(VacDay-UseVacDay as float) AS "RemainVacDay" , SortKey AS "SortKey" , TargetYear AS "TargetYear" FROM ( SELECT A.UR_Code , B.DisplayName , Fn_BaseGetDictionary_S(#{lang}, NVL(A.DeptName, B.MultiDeptName)) AS DeptName , B.JobPositionName , NVL(C.CodeName, G.CodeName) AS VacName , Year , UseStartDate , UseEndDate , VacKind , cast(cast(A.VacDay as DECIMAL(9,3)) as float) AS VacDay , VmComment , CASE WHEN RegisterName is not null THEN RegisterName ELSE A.RegisterCode END AS RegisterName , A.RegisterCode AS RegisterCode , TO_CHAR(A.RegistDate, 'YYYY-MM-DD') AS RegistDate , TO_CHAR(TO_DATE(A.UseStartDate, 'YYYYMMDD'), 'YYYY.MM.DD') || ' ~ ' || TO_CHAR(TO_DATE(A.UseEndDate, 'YYYYMMDD'), 'YYYY.MM.DD') AS ExpDate , A.UseVacDay AS UseVacDay , NVL(C.SortKey, G.SortKey) AS SortKey , TargetYear , B.CompanyCode FROM ( SELECT A.UserCode , B.CompanyCode , B.MultiDeptName , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , A.TargetYear , CONCAT(A.TargetYear,TO_CHAR(TO_DATE(REPLACE(A.EnterDate,'-',''),'YYYYMMDD'),'MMDD')) AS TargetSdate , TO_CHAR(TO_DATE(CONCAT(A.TargetYear,TO_CHAR(TO_DATE(REPLACE(A.EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') + (INTERVAL '1' YEAR) - (INTERVAL '1' DAY),'YYYYMMDD') AS TargetEdate FROM (select UserCode, EnterDate, MultiDisplayName, RetireDate, SortKey , CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR)) END AS TargetYear from sys_object_user where EnterDate is not null) A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' ) B LEFT JOIN (select P.*, (SELECT cast(NVL(SUM(NVL(VacDay,0.0)),0.0) as float) FROM vm_vacationinfo V ,(SELECT B.Code , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) ELSE B.CodeName END AS CodeName , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, A.MultiCodeName) ELSE A.CodeName END AS GroupCodeName ,A.Code as GroupCode ,B.SortKey ,B.CodeID FROM (select * from sys_base_code WHERE IsUse = 'Y' AND DomainID = 0 ) A, sys_base_code B WHERE A.CodeID = B.ReservedInt AND B.IsUse = 'Y' AND B.Reserved1 = '+'
AND B.DomainID = #{domainID} ) C where V.UR_Code = P.UR_Code AND V.VacFlag = C.Code AND V.VACFLAG = P.VACKIND AND C.GroupCode <> 'PUBLIC' AND TO_DATE(V.Sdate,'YYYY-MM-DD') >= TO_DATE(P.UseStartDate,'YYYYMMDD') AND TO_DATE(V.Edate,'YYYY-MM-DD') <= TO_DATE(P.UseEndDate,'YYYYMMDD') ) AS UseVacDay from vm_vacationplan P where VacKind <> 'PUBLIC') A ON A.UR_Code = B.UserCode and A.UseStartDate >= B.TargetSdate and A.UseEndDate <= B.TargetEdate LEFT JOIN (SELECT B.Code , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) ELSE B.CodeName END AS CodeName ,A.Code as GroupCode ,B.SortKey ,B.CodeID FROM (select * from sys_base_code WHERE IsUse = 'Y' AND DomainID = 0 ) A, sys_base_code B WHERE A.CodeID = B.ReservedInt AND B.IsUse = 'Y' AND B.DomainID = #{domainID}) C ON A.VacKind = C.GroupCode LEFT JOIN (SELECT UserCode ,Fn_BaseGetDictionary_S(#{lang}, MultiDisplayName) AS RegisterName FROM sys_object_user ) E ON A.RegisterCode = E.UserCode LEFT JOIN (select Code, Fn_BaseGetDictionary_S(#{lang}, NVL(MultiCodeName, CodeName)) AS CodeName, SortKey from sys_base_code where IsUse = 'Y' and DomainID = #{domainID} and CodeGroup = 'VACATION_TYPE') G ON G.Code = A.VacKind WHERE 1=1 AND (C.CodeName is not null OR G.CodeName is not null)
AND Companycode = #{domainCode}
AND VacKind = #{vacKind}
AND UseStartDate = #{sDate}
AND UseEndDate = #{eDate}
AND UR_Code = #{urCode}
AND CodeID = #{codeID}
AND DeptName LIKE '%' || #{schTxt} || '%'
AND UR_Code = #{schTxt}
AND DisplayName LIKE '%' || #{schTxt} || '%'
SortKey, DisplayName, VacKind, UseStartDate ASC
DisplayName ExtVacName RegistDate (VacDay-UseVacDay) ExtUseVacDay ExpDate ExtVacDay ExtReason RegisterName DeptName DisplayName, VacKind, RegistDate
ASC DESC
) TBL

SELECT VT.UR_Code AS "UR_Code" ,DeptName AS "DeptName" ,CodeName AS "CodeName" ,UseStartDate AS "Sdate" ,UseEndDate AS "Edate" ,VacKind AS "VacKind" ,CAST(VacDay AS FLOAT) as "VacDay" ,CAST(NVL(UseVacDay,0.0) AS FLOAT) as "VacDayUse" ,CAST(VacDay - NVL(UseVacDay,0.0) AS FLOAT) as "VacDayRemain" FROM ( SELECT UR_Code,DeptName,CodeName,UseStartDate,UseEndDate,VacKind,VacDay, UseVacDay FROM (SELECT B.Code , CASE WHEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) is not null THEN Fn_BaseGetDictionary_S(#{lang}, B.MultiCodeName) ELSE B.CodeName END AS CodeName ,A.Code as GroupCode ,A.CodeName as GroupCodeName ,B.SortKey ,B.CodeID FROM
(select * from sys_base_code WHERE IsUse = 'Y' AND DomainID = 0 ) A, sys_base_code B WHERE A.CodeID = B.ReservedInt AND B.IsUse = 'Y' AND B.CodeID = #{codeID} AND B.DomainID = #{domainID}) TY , ( SELECT UR_Code, DeptName, VacKind , VacDay, UseVacDay, USESTARTDATE, USEENDDATE FROM ( SELECT P.UR_Code , P.DeptName , P.VacKind , P.VacDay , P.USESTARTDATE , P.USEENDDATE , CASE WHEN P.VACKIND = 'PUBLIC' THEN ( select NVL(SUM(VacDay), 0.0) from vm_vacationinfo A, (select BA.Code, BA.ReservedInt, BA.CodeGroup, BB.Code as VacKindCode from (select * from sys_base_code where IsUse = 'Y' and DomainID = #{domainID} and Reserved1 = '+' and CodeGroup = 'VACATION_TYPE') BA , (select * from sys_base_code where IsUse = 'Y' and DomainID = '0' and CodeGroup = 'VACATION_KIND') BB where BA.ReservedInt = BB.CodeID ) B where A.VacFlag = B.Code AND B.VacKindCode = P.VacKind AND TO_DATE(A.SDATE, 'YYYY-MM-DD') >= TO_DATE(P.UseStartDate, 'YYYYMMDD') AND TO_DATE(A.EDATE, 'YYYY-MM-DD') <= TO_DATE(P.UseEndDate, 'YYYYMMDD') AND A.UR_CODE = P.UR_CODE ) ELSE ( select NVL(SUM(VacDay), 0.0) from vm_vacationinfo A, (select BA.Code, BA.ReservedInt, BA.CodeGroup, BB.Code as VacKindCode from (select * from sys_base_code where IsUse = 'Y' and Reserved1 = '+' and DomainID = #{domainID} and CodeGroup = 'VACATION_TYPE') BA , (select * from sys_base_code where IsUse = 'Y' and DomainID = '0' and CodeGroup = 'VACATION_KIND') BB where BA.ReservedInt = BB.CodeID ) B where A.VacFlag = B.Code AND B.Code = P.VacKind AND TO_DATE(A.SDATE, 'YYYY-MM-DD') >= TO_DATE(P.UseStartDate, 'YYYYMMDD') AND TO_DATE(A.EDATE, 'YYYY-MM-DD') <= TO_DATE(P.UseEndDate, 'YYYYMMDD') AND A.UR_CODE = P.UR_CODE ) END AS UseVacDay FROM (SELECT U.UserCode , CASE WHEN CFG.CreateMethod = 'J' THEN CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'M