AND USERCODE = #{urCode}
AND USERCODE in
#{item}
) U LEFT JOIN vm_config CFG ON CFG.CompanyCode = #{domainCode} LEFT JOIN sys_object_user_basegroup UB ON U.UserCode = UB.UserCode AND UB.JobType = 'Origin' ) UR LEFT JOIN vm_vacationplan P ON P.UR_CODE = UR.USERCODE AND TO_DATE(P.USESTARTDATE,'YYYYMMDD') >= UR.TargetYearFrom AND TO_DATE(P.USESTARTDATE,'YYYYMMDD') <= (UR.TargetYearFrom + (INTERVAL '1' YEAR ) - (INTERVAL '1' DAY )) WHERE 1=1
and TO_DATE(#{calMonth}, 'YYYY-MM') between TO_DATE(SUBSTR(UseStartDate,1,6), 'YYYYMM') and TO_DATE(SUBSTR(UseEndDate,1,6), 'YYYYMM')
) P ) PL WHERE TY.Code = PL.VacKind OR (TY.GroupCode = 'PUBLIC' AND PL.VACKIND = 'PUBLIC') ) VT

/* groupware.vacation.checkVacationExtraPlan */
SELECT UR_Code AS "UR_Code" , DisplayName AS "DisplayName" , DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , Year AS "Year" ,CASE WHEN VacDay < 0 THEN 0.0 ELSE VacDay END AS "VacDay" ,CASE WHEN TotVacDay < 0 THEN 0.0 ELSE TotVacDay END AS "TotVacDay" ,(select ExistDate from (select ROWNUM , TO_CHAR(TO_DATE(UseStartDate,'YYYYMMDD'),'YYYY.MM.DD')||'~'||TO_CHAR(TO_DATE(UseEndDate,'YYYYMMDD'),'YYYY.MM.DD') AS ExistDate from vm_vacationplan where YEAR = #{year} and VacKind = #{vacKind} and UR_Code in
#{item}
and (TO_DATE(#{sDate},'YYYY-MM-DD') between TO_DATE(UseStartDate,'YYYYMMDD') and TO_DATE(UseEndDate,'YYYYMMDD') or TO_DATE(#{eDate},'YYYY-MM-DD') between TO_DATE(UseStartDate,'YYYYMMDD') and TO_DATE(UseEndDate,'YYYYMMDD')) and ROWNUM = 1) ) AS "ExistDate" FROM ( SELECT B.UserCode AS UR_Code , B.DisplayName AS DisplayName , Fn_BaseGetDictionary_S(NULL, B.MultiDeptName) AS DeptName , B.JobPositionName AS JobPositionName , Year AS Year , cast(SUM(NVL(A.VacDay,0.0)) as float) AS VacDay , cast(SUM(NVL(A.VacDay,0.0))+#{vacDay} as float) AS TotVacDay FROM ( SELECT A.UserCode , B.CompanyCode , B.MultiDeptName , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName FROM sys_object_user A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' WHERE A.UserCode in
#{item}
AND B.Companycode = #{domainCode}
) B LEFT JOIN (select UR_Code, YEAR, VacKind, DeptName, SUM(VacDay) as VacDay from vm_vacationplan where YEAR = #{year} and VacKind <> 'PUBLIC' group by UR_Code, YEAR, VacKind, DeptName) A ON A.UR_Code = B.UserCode
GROUP BY B.UserCode, DisplayName, Year, B.JobPositionName, B.MultiDeptName, B.JobPositionName ) TBL

