/* groupware.vacation.selectVacationCancelCheck */
SELECT A.VacationInfoID AS "VacationInfoID" , A.UR_Code AS "UR_Code" , B.DisplayName AS "DisplayName" , A.APPDATE , A.ENDDATE , A.GUBUN , Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) AS "GubunName" , A.VacYear AS "VacYear" , A.Sdate AS "Sdate" , A.Edate AS "Edate" , A.VacFlag AS "VacFlag" , A.VacOffFlag AS "VacOffFlag" , Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) AS "VacFlagName" , cast(cast(TO_CHAR(A.VacDay) as DECIMAL(9,3)) as float) AS "VacDay" , cast(NVL(B.VacDayTot,0.0) as float) AS "VacDayTot" , cast(cast(TO_CHAR(NVL(SUM(V.VacDayUse),0.0)) as DECIMAL(9,3)) as float) AS "VacDayUse" , cast(cast(TO_CHAR(NVL(B.VacDayTot-SUM(V.VacDayUse),0.0)) as DECIMAL(9,3)) as float) AS "VacDayRemain" , A.Reason AS "Reason" , A.WorkItemID AS "WorkItemID" , A.ProcessID AS "ProcessID" , B.DeptName AS "DeptName" , JobPositionName AS "JobPositionName" , (CASE WHEN (A.GUBUN = 'VACATION_CANCEL' OR A.GUBUN = 'VACATION_PUBLIC' OR A.GUBUN = 'VACATION_PUBLIC_CANCEL' ) THEN '(' || Fn_BaseGetDictionary_S(#{lang}, C.MultiCodeName) || ')' || Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) ELSE Fn_BaseGetDictionary_S(#{lang}, D.MultiCodeName) END ) AS VACTEXT FROM (select * from vm_vacationinfo) A LEFT JOIN ( SELECT A.UserCode , B.CompanyCode , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , SUM(VacDay) AS VacDayTot , A.SDATE, A.EDATE FROM ( select UserCode, DisplayName, MULTIDISPLAYNAME, ENTERDATE ,CASE WHEN CREATEMETHOD = 'J' THEN TO_DATE(CONCAT(TargetYear,TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') ELSE TO_DATE(CONCAT(TargetYear, '0101'),'YYYYMMDD') END as Sdate , CASE WHEN CREATEMETHOD = 'J' THEN TO_DATE(CONCAT(TargetYear,TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') + (INTERVAL '1' YEAR) - (INTERVAL '1' DAY ) ELSE TO_DATE(CONCAT(TargetYear, '1231'),'YYYYMMDD') END as Edate from ( select UserCode, DisplayName, MULTIDISPLAYNAME, ENTERDATE, CFG.CREATEMETHOD ,CASE WHEN CFG.CREATEMETHOD = 'J' THEN CASE WHEN TO_DATE(CONCAT(#{year},TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') <= TO_DATE(CONCAT(#{year},TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(#{year},'0101'),'YYYYMMDD') - (INTERVAL '1' YEAR)) END ELSE TO_NUMBER(#{year}) END AS TargetYear from SYS_OBJECT_USER left join VM_CONFIG CFG ON CFG.COMPANYCODE = #{CompanyCode}) ) A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND JobType = 'Origin' LEFT JOIN vm_vacationplan E ON A.UserCode = E.UR_Code and TO_DATE(E.USESTARTDATE,'YYYYMMDD') >= A.SDATE and TO_DATE(E.USEENDDATE,'YYYYMMDD') <= A.EDATE group by A.UserCode, B.CompanyCode, DisplayName, JobPositionName, A.MultiDisplayName, B.MultiDeptName, B.MultiJobPositionName, A.SDATE, A.EDATE ) B ON A.UR_Code = B.UserCode LEFT JOIN sys_base_code C ON A.GUBUN = C.Code AND C.CodeGroup = 'VACATION_GUBUN' AND C.DomainID = 0 LEFT JOIN sys_base_code D ON A.VacFlag = D.Code AND D.CodeGroup = 'VACATION_TYPE' AND D.DomainID =#{domainID} LEFT JOIN (select UR_Code, VACDAY as VacDayUse, SDATE, EDATE from vm_vacationinfo ) V ON V.UR_Code = A.UR_Code AND TO_DATE(V.SDATE,'YYYY-MM-DD') >= B.SDATE AND TO_DATE(V.EDATE,'YYYY-MM-DD') <= B.EDATE WHERE A.VacationInfoID in
#{item}
AND B.Companycode = #{domainCode}
group by A.VacationInfoID, A.UR_Code, B.DisplayName, A.APPDATE , A.ENDDATE , A.GUBUN, C.MULTICODENAME , A.VacYear , A.Sdate , A.Edate , A.VacFlag , A.VacOffFlag , D.MultiCodeName, A.Reason , A.WorkItemID , A.ProcessID , B.DeptName, JobPositionName, A.VacDay, B.VacDayTot
A.VacationInfoID DESC

