mapper.delete:
/* groupware.vacation.deleteVacationPlanHist */
delete from vm_plan_hist
WHERE VacKind = 'PUBLIC'
AND UR_Code in (
SELECT A.UserCode
FROM sys_object_user A
LEFT JOIN (SELECT UserCode, CompanyName, CompanyCode, DeptCode, MultiDeptName
FROM sys_object_user_basegroup
WHERE JobType = 'Origin'
) B ON A.UserCode = B.UserCode
WHERE B.CompanyCode = #{CompanyCode}
)

mapper.update:
/* groupware.vacation.updateAutoIncreaseMonthlyVacationV2 */

	UPDATE vm_vacationplan
	SET VacDay = #{vacDay}
	, LongVacDay = #{vacDay}-LastVacDay
	WHERE UR_Code = #{urCode}
	AND  VacKind = #{vacKind}
	AND  UseStartDate = #{useStartDate}
	AND  UseEndDate = #{useEndDate}

mapper.select:
/* groupware.vacation.selectVacationPromotionTargetUserInfo */
SELECT UserCode AS "UserCode" , DisplayName AS "UR_Name" , JobPositionName AS "JobPositionName" , EnterDate AS "EnterDate" , RetireDate AS "RetireDate" , CreateMethod AS "CreateMethod" , JobPositionSortKey AS "JobPositionSortKey" , Tyear AS TYEAR , EXTRACT(YEAR FROM SYSDATE) AS YYYY , IsOneYear AS "IsOneYear" , VacDate AS "VacDateFrom" , fn_attend_getPromotionDateRage('T','Code1',VacDate, #{domainCode}) AS "VacDateTo" , OneDate AS "OneDateFrom" , fn_attend_getPromotionDateRage('T','Code2',OneDate, #{domainCode}) AS "OneDateTo" , TO_CHAR(OneDate,'YYYY-MM-DD') || ' ~ ' || fn_attend_getPromotionDateRage('T','Code2',OneDate, #{domainCode}) AS "OneDateRange" , TwoDate AS "TwoDateFrom" , fn_attend_getPromotionDateRage('T','Code3',TwoDate, #{domainCode}) AS "TwoDateTo" , TO_CHAR(TwoDate,'YYYY-MM-DD') || ' ~ ' || fn_attend_getPromotionDateRage('T','Code3',OneDate, #{domainCode}) AS "TwoDateRange" , LessVacDate AS "LessVacDateFrom" , fn_attend_getPromotionDateRage('T','Code4',LessVacDate, #{domainCode}) AS "LessVacDateTo" , LessOneDate9 AS "LessOneDate9From" , fn_attend_getPromotionDateRage('T','Code5',LessOneDate9, #{domainCode}) AS "LessOneDate9To" , TO_CHAR(LessOneDate9,'YYYY-MM-DD') || ' ~ ' || fn_attend_getPromotionDateRage('T','Code5',LessOneDate9, #{domainCode}) AS "LessOneDate9Range" , LessTwoDate9 AS "LessTwoDate9From" , fn_attend_getPromotionDateRage('T','Code6',LessTwoDate9, #{domainCode}) AS "LessTwoDate9To" , TO_CHAR(LessTwoDate9,'YYYY-MM-DD') || ' ~ ' || fn_attend_getPromotionDateRage('T','Code6',LessTwoDate9, #{domainCode}) AS "LessTwoDate9Range" , LessOneDate2 AS "LessOneDate2From" , fn_attend_getPromotionDateRage('T','Code7',LessOneDate2, #{domainCode}) AS "LessOneDate2To" , TO_CHAR(LessOneDate2,'YYYY-MM-DD') || ' ~ ' || fn_attend_getPromotionDateRage('T','Code7',LessOneDate2, #{domainCode}) AS "LessOneDate2Range" , LessTwoDate2 AS "LessTwoDate2From" , fn_attend_getPromotionDateRage('T','Code8',LessTwoDate2, #{domainCode}) AS "LessTwoDate2To" , TO_CHAR(LessTwoDate2,'YYYY-MM-DD') || ' ~ ' || fn_attend_getPromotionDateRage('T','Code8',LessTwoDate2, #{domainCode}) AS "LessTwoDate2Range" FROM ( SELECT A.UserCode, A.MailAddress , Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS DisplayName , Fn_BaseGetDictionary_S(#{lang}, B.MultiJobPositionName) AS JobPositionName , A.EnterDate , A.RetireDate , A.SortKey , B.DeptCode , B.JobPositionSortKey , V.CreateMethod , Tyear , IsOneYear ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code1' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code1' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code1' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS VacDate ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code2' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code2' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code2' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS OneDate ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code3' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code3' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code3' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS TwoDate ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code4' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code4' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code4' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS LessVacDate ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code5' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code5' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code5' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS LessOneDate9 ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code6' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code6' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code6' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS LessTwoDate9 ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code7' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code7' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code7' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS LessOneDate2 ,( CASE WHEN IsOneYear = 0 THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code8' ,ADD_MONTHS(EnterDate, 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'J' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code8' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(EnterDate,'MMDD')),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') WHEN V.CreateMethod = 'F' THEN TO_DATE(fn_attend_getPromotionDateRage('F','Code8' ,ADD_MONTHS(TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD'), 12) ,#{domainCode}),'YYYY-MM-DD') END ) AS LessTwoDate2 FROM (SELECT UserCode , MultiDisplayName , MailAddress , CASE WHEN TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD'),'MMDD')),'YYYYMMDD') < TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),TO_CHAR(SYSDATE,'MMDD')),'YYYYMMDD') THEN EXTRACT(YEAR FROM TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD')) ELSE EXTRACT(YEAR FROM TO_DATE(CONCAT(EXTRACT(YEAR FROM SYSDATE),'0101'),'YYYYMMDD') - (INTERVAL '1' DAY)) END AS Tyear , TO_DATE(REPLACE(EnterDate, '-', ''), 'YYYYMMDD') AS EnterDate , RetireDate , SortKey , CASE WHEN TO_CHAR(TO_DATE(REPLACE(EnterDate,'-',''),'YYYYMMDD') + (INTERVAL '12' MONTH), 'YYYYMMDD') <= TO_CHAR(SYSDATE, 'YYYYMMDD') -- 1 THEN 1 ELSE 0 END as IsOneYear FROM sys_object_user) A LEFT JOIN sys_object_user_basegroup B ON A.UserCode = B.UserCode AND B.JobType = 'Origin' LEFT JOIN vm_config V ON V.CompanyCode = B.CompanyCode WHERE B.CompanyCode = #{domainCode} ) A WHERE 1=1 AND A.RetireDate IS NULL AND A.DeptCode NOT LIKE '%RetireDept' AND A.EnterDate IS NOT NULL
AND A.UserCode = #{urCode}

/* legacy.formCmmFunction.selectVacationInfo */ SELECT NVL(CASE WHEN ROWCNT = 0 THEN 0 ELSE EXIST END,0) AS cnt FROM (SELECT a.ROWCNT, CASE WHEN #{VacOffFlag}='AM' OR #{VacOffFlag}='PM' THEN CASE WHEN cnt = 0 OR cnt is null THEN 0 WHEN LENGTH2(#{STime}) > 0 AND LENGTH2(#{ETime}) > 0 THEN CASE WHEN (CAST(REPLACE(#{STime},':','') AS INTEGER)+1 >= CAST(REPLACE(StartTime,':','') AS INTEGER) AND CAST(REPLACE(#{STime},':','') AS INTEGER)+1 <= CAST(REPLACE(EndTime,':','') AS INTEGER)) OR (CAST(REPLACE(#{ETime},':','') AS INTEGER)-1 >= CAST(REPLACE(StartTime,':','') AS INTEGER) AND CAST(REPLACE(#{ETime},':','') AS INTEGER)-1 <= CAST(REPLACE(EndTime,':','') AS INTEGER)) THEN 1 ELSE 0 END WHEN LENGTH2(#{STime}) = 0 AND LENGTH2(#{ETime}) = 0 THEN CASE WHEN LENGTH2(StartTime) > 0 AND LENGTH2(EndTime) > 0 AND VacOffFlag='PM' THEN 1 WHEN LENGTH2(StartTime) = 0 AND LENGTH2(EndTime) = 0 AND VacOffFlag=#{VacOffFlag} THEN 1 ELSE 0 END ELSE 1 END ELSE NVL(cnt,0) END AS EXIST
FROM (select COUNT(*) AS ROWCNT, MAX(UR_Code) UR_CODE FROM (select * from vm_vacationinfo_day) vm WHERE TO_DATE(VacDate,'YYYY-MM-DD') BETWEEN TO_DATE(#{StartDate},'YYYY-MM-DD') AND TO_DATE(#{EndDate},'YYYY-MM-DD') AND UR_Code = #{UserCode} AND (GUBUN != 'VACATION_CANCEL' and GUBUN != 'VACATION_PUBLIC_CANCEL')
AND (VacOffFlag is null OR VacOffFlag = #{VacOffFlag})
) a left join (select 1 cnt, VacDate, UR_Code,GUBUN,VacDay, StartTime, EndTime, VacOffFlag from vm_vacationinfo_day WHERE TO_DATE(VacDate,'YYYY-MM-DD') BETWEEN TO_DATE(#{StartDate},'YYYY-MM-DD') AND TO_DATE(#{EndDate},'YYYY-MM-DD') AND UR_Code = #{UserCode} AND (GUBUN != 'VACATION_CANCEL' and GUBUN != 'VACATION_PUBLIC_CANCEL')
AND (VacOffFlag is null OR VacOffFlag = #{VacOffFlag})
) vm ON a.UR_CODE = vm.UR_CODE ) T

    SELECT COUNT(1) - (TO_DATE(#{EndDate},'YYYY-MM-DD') - TO_DATE(#{StartDate},'YYYY-MM-DD') + 1) AS invalidcnt
    FROM vm_vacationinfo_day
	WHERE UR_Code = #{UserCode}
	AND VacFlag = #{VacFlag}
	AND VacOffFlag = #{VacOffFlag}
	AND TO_DATE(VacDate,'YYYY-MM-DD') BETWEEN TO_DATE(#{StartDate},'YYYY-MM-DD') AND TO_DATE(#{EndDate},'YYYY-MM-DD')
	AND (GUBUN != 'VACATION_CANCEL' and GUBUN != 'VACATION_PUBLIC_CANCEL')

mapper.update:
/* groupware.vacation.updateLastLongVacDay */

		SELECT fn_GetLastVacDay(#{userCode}, #{year}, #{domainCode}) AS "LastVacDay" FROM dual

	UPDATE vm_vacationplan
	   SET LastVacDay = #{LastVacDay} , 
	       LongVacDay = VacDay-#{LastVacDay}
	 WHERE UR_Code = #{userCode}
	   AND YEAR = #{year}
		AND VacKind = 'PUBLIC'
